import studio from '@theatre/studio';
import { getProject, types } from '@theatre/core';
import { useState, useEffect } from 'react';
import styled from 'styled-components';
import projectState from '../json/state.json';

const Container = styled.div`
  width: 100%;
  height: 100vh;
  position: relative;
  outline: 5px solid red;
  z-index: -1;
  overflow: hidden;
`;

const BasicBox = styled.div`
  position: absolute;
  width: 300px;
  height: 20px;
  background: white;
  outline: 3px solid black;
  z-index: 10000;
  padding: 20px;
`;

export default function ScrollDemo() {
  studio.initialize();

  const project = getProject('deepfake demo', { state: projectState });
  const sheet = project.sheet('Scene', 'default');

  studio.ui.hide();

  project.ready.then(() =>
    sheet.sequence.play({
      iterationCount: 1,
    })
  );

  const [preData, setPreData] = useState([]); //上一筆舊資料，要與 local-storage 一致

  const scrollOffset = useScrollOffset(); // 自訂的 hook，用於獲取滾動位置

  //從 local-storage 取得舊資料
  useEffect(() => {
    const storedBoxes = localStorage.getItem('theatre-data');
    if (storedBoxes) {
      setPreData(JSON.parse(storedBoxes));
    }
  }, []);

  //render 出既有的元件
  useEffect(() => {
    preData
      .filter((item) => item.type === 'box')
      .forEach((item) => {
        const box = sheet.object(item.id, {
          position: {
            x: 0,
            y: 0,
          },
          bgColor: types.string('#ffffff'),
        });

        const element = document.getElementById(item.id);

        box.onValuesChange((newValue) => {
          element.style.left = newValue.position.x + 'px';
          element.style.top = newValue.position.y + 'px';
          element.style.backgroundColor = newValue.bgColor;
        });
      });
  });

  // 更新 sequence 的位置
  useEffect(() => {
    const sequenceLength = sheet.sequence.pointer.length;
    sheet.sequence.position = scrollOffset * sequenceLength;
  }, [scrollOffset, sheet]);

  return (
    <>
      <Container id='item-container'>
        {preData.map((box, index) => (
          <BasicBox key={index} id={box.id} style={box.style}>
            {box.content}
          </BasicBox>
        ))}
      </Container>
    </>
  );
}

// 自訂的 hook，用於獲取滾動位置
function useScrollOffset() {
  const [scrollOffset, setScrollOffset] = useState(0);

  useEffect(() => {
    function handleScroll() {
      const { scrollTop, scrollHeight, clientHeight } =
        document.documentElement;
      const maxScrollOffset = scrollHeight - clientHeight;
      const currentScrollOffset = scrollTop / maxScrollOffset;
      setScrollOffset(currentScrollOffset);
    }

    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return scrollOffset;
}
