import React, { useState } from 'react';
import styled from 'styled-components';

const Wrapper = styled.div`
  width: 100%;
  height: 100vh;
  background: black;
  opacity: 1;
  display: flex;
  justify-content: center;
  position: fixed;
  top: 0;
  left: 0;
  z-index: 200;
`;

const Form = styled.div`
  width: 30%;
  height: 50%;
  background: white;
  border: 10px;
  padding: 20px;
  outline: 3px solid black;
  margin: 70px auto;

  input {
    width: 30%;
    margin-bottom: 20px;
  }

  textarea {
    width: 90%;
    height: 200px;
  }

  button {
    padding: 5px;
    background: black;
    color: white;
    outline: none;
    cursor: pointer;
    margin-top: 10px;
    margin-right: 10px;
  }
`;

export default function BgLightBox({
  setShowBgLightBox,
  storedData,
  setStoredData,
}) {
  const [bgURL, setBgUrl] = useState('');
  const [bgID, setBgID] = useState('');

  const addBgElement = (id = '', url = '') => {
    const updatedElementData = [
      ...storedData,
      { id: id, url: url, type: 'BACKGROUND' },
    ];

    localStorage.setItem('theatre-data', JSON.stringify(updatedElementData));
    setStoredData(updatedElementData);
  };

  return (
    <Wrapper>
      <Form>
        <h2>新增 Background 元件 </h2>

        <span>物件 ID (勿與之前的重複)： </span>
        <input
          placeholder='中/英/數字皆可'
          value={bgID}
          onChange={(e) => setBgID(e.target.value)}
        />

        <textarea
          placeholder='背景圖片連結'
          value={bgURL}
          onChange={(e) => setBgUrl(e.target.value)}
        />

        <button
          onClick={() => {
            setShowBgLightBox(false);
            addBgElement(bgID, bgURL);
          }}
        >
          確定
        </button>

        <button
          onClick={() => {
            setShowBgLightBox(false);
          }}
        >
          取消
        </button>
      </Form>
    </Wrapper>
  );
}
