import studio from '@theatre/studio';
import React, { useMemo, useState, useEffect } from 'react';
import styled from 'styled-components';
import useDrag from '../../hooks/use-drag';
import { initialConfig } from '../../constants';

const Video = styled.video`
  position: absolute;
  display: block;
  height: auto;
  box-sizing: border-box;
`;

export default function VideoElement({
  id,
  sheet,
  stageWidth,
  draggable = true,
}) {
  const object = sheet.object(id, {
    ...initialConfig.VIDEO,
  });

  // Drag setting ----------------------
  const [divRef, setDivRef] = useState(null);
  const stageHeight = window.innerHeight;

  const dragOpts = useMemo(() => {
    let scrub;
    let initial;
    let firstOnDragCalled = false;
    return {
      onDragStart() {
        scrub = studio.scrub();
        initial = object.value;
        firstOnDragCalled = false;
      },

      onDrag(x, y) {
        const percentX = (x / stageWidth) * 100;
        const percentY = (y / stageHeight) * 100;

        if (!firstOnDragCalled) {
          studio.setSelection([object]);
          firstOnDragCalled = true;
        }
        scrub.capture(({ set }) => {
          set(object.props, {
            ...initial,
            position: {
              x: percentX + initial.position.x,
              y: percentY + initial.position.y,
            },
          });
        });
      },
      onDragEnd(dragHappened) {
        if (dragHappened) {
          scrub.commit();
        } else {
          scrub.discard();
        }
      },
      lockCursorTo: 'move',
    };
  }, []);

  draggable && useDrag(divRef, dragOpts);

  // Style setting ----------------------
  const [style, setStyle] = useState({});
  // const [source, setSource] = useState('');

  const handleVideoEnded = () => {
    // 影片播放結束時，將 isPlaying 設定為 false，以停止影片播放
    sheet.setValue(id, { isPlaying: false });
  };

  useEffect(() => {
    object.onValuesChange((newValue) => {
      if (newValue.isPlaying) {
        divRef.current.play();
      } else {
        divRef.current.pause();
      }

      // setSource(newValue.source);
      setStyle({
        left: `${newValue.position.x}%`,
        top: `${newValue.position.y}%`,
        display: `${newValue.visible ? 'block' : 'none'}`,
        width: `${newValue.size.width}px`,
        height: `${newValue.size.height}px`,
        zIndex: `${newValue.zIndex}`,
        transform: `scale(${newValue.scale})`,
      });
    });
  }, [object]);

  return (
    <Video
      muted
      autoPlay
      loop
      controls
      ref={setDivRef}
      onEnded={handleVideoEnded}
      style={style}
      onClick={() => {
        studio.setSelection([object]);
      }}
    >
      <source src='https://storage.cloud.google.com/v3-statics-staging.mirrormedia.mg/files/20161130141317-3863a0e1b9a81cc40dee4f281fc70274.mp4' />
    </Video>
  );
}
