import studio from '@theatre/studio';
import { getProject, types, val } from '@theatre/core';
import { useState, useEffect, useRef } from 'react';
import styled from 'styled-components';
import projectState from '../json/state.json';
import { over } from 'lodash';

const defaultViewportHeight = '100vh';

const ViewBox = styled.div`
  position: relative;
  width: 100%;
  height: 100vh;
  overflow: hidden;
`;

const TheatreContainer = styled.div`
  width: 100%;
  height: 100%;
  outline: 5px solid red;
  overflow-y: auto;
  padding-right: 15px; //to hide scroll-bar
  overflow-y: ${(props) => (props.scrollEnabled ? 'auto' : 'hidden')};
`;

const TheatreElements = styled.div`
  width: 100%;
  height: 100vh;
  outline: 5px solid blue;
  background: grey;
  position: sticky;
  top: 0;
  left: 0;
`;

const ScrollHeight = styled.div`
  background: pink;
  width: 100%;
  height: 200vh;
`;

const BasicBox = styled.div`
  position: absolute;
  width: 300px;
  height: 20px;
  background: white;
  outline: 3px solid black;
  z-index: 10000;
  padding: 20px;
`;

// 下面參考 scrollable-video ---------------------------------

//代表滑到這個框框的最上方
const PositionStarter = styled.div`
  width: 20px;
  height: 20px;
  position: absolute;
  top: 0;
  right: 0;
  opacity: 0;
`;

const PositionEnd = styled.div`
  width: 20px;
  height: 20px;
  position: absolute;
  bottom: 0;
  right: 0;
  opacity: 0;
`;

// 對位用，對齊左上角
// const Box100vh = styled.div`
//   width: 0;
//   position: fixed;
//   height: ${defaultViewportHeight};
//   left: 0;
//   top: 0;
//   background: black;
//   z-index: 100000;
//   /* opacity: 0; */
// `;

const Other = styled.div`
  width: 100%;
  height: 100vh;
`;

export default function ScrollDemo() {
  // theatre setting
  studio.initialize();
  const project = getProject('deepfake demo', { state: projectState });
  const sheet = project.sheet('Scene', 'default');

  //操作 UI 顯示
  studio.ui.hide();
  project.ready.then(() => sheet.sequence.pause);

  const sequenceLength = val(sheet.sequence.pointer.length);

  // render 元件於畫面中 -------------------------------------
  const [preData, setPreData] = useState([]);

  useEffect(() => {
    const storedBoxes = localStorage.getItem('theatre-data');
    if (storedBoxes) {
      setPreData(JSON.parse(storedBoxes));
    }
  }, []);

  useEffect(() => {
    preData
      .filter((item) => item.type === 'box')
      .forEach((item) => {
        const box = sheet.object(item.id, {
          position: {
            x: 0,
            y: 0,
          },
          bgColor: types.string('#ffffff'),
        });

        const element = document.getElementById(item.id);

        box.onValuesChange((newValue) => {
          element.style.left = newValue.position.x + 'px';
          element.style.top = newValue.position.y + 'px';
          element.style.backgroundColor = newValue.bgColor;
        });
      });
  });

  // scroll 事件 -------------------------------------
  const containerRef = useRef(null);
  const positionStarterRef = useRef(null);

  //滑動的離頂端有多少距離
  const [scrollPosition, setScrollPosition] = useState(0);

  useEffect(() => {
    const container = containerRef.current;
    const handleScroll = () => {
      const currentPosition = container.scrollTop;
      setScrollPosition(currentPosition);
    };

    container.addEventListener('scroll', handleScroll);

    return () => {
      container.removeEventListener('scroll', handleScroll);
    };
  }, []);

  // 透過 scrollPosition 去決定 sequence 的位置
  useEffect(() => {
    const newPosition = (scrollPosition / 2000) * sequenceLength;
    sheet.sequence.position = newPosition;
  }, [scrollPosition]);

  const [scrollEnabled, setScrollEnabled] = useState(false);

  useEffect(() => {
    const positionStarter = positionStarterRef.current;

    const handleScrollEnable = () => {
      const positionStarterRect = positionStarter.getBoundingClientRect();

      setScrollEnabled(
        positionStarterRect.top <= 0 && positionStarterRect.left <= 0
      );
    };

    handleScrollEnable(); // Check initial visibility

    window.addEventListener('scroll', handleScrollEnable);

    return () => {
      window.removeEventListener('scroll', handleScrollEnable);
    };
  }, []);

  return (
    <>
      <Other />
      <PositionStarter ref={positionStarterRef} />
      <ViewBox>
        <TheatreContainer
          id='item-container'
          ref={containerRef}
          scrollEnabled={scrollEnabled}
        >
          <TheatreElements id='content'>
            {preData.map((box, index) => (
              <BasicBox key={index} id={box.id} style={box.style}>
                {box.content}
              </BasicBox>
            ))}
          </TheatreElements>

          <ScrollHeight />
        </TheatreContainer>
      </ViewBox>
      <PositionEnd ref={positionEndRef} />

      <Other />
    </>
  );
}
