import styled from 'styled-components';
import React, { useState } from 'react';

const Wrapper = styled.div`
  width: 100%;
  height: 100vh;
  background: black;
  opacity: 0.9;
  display: flex;
  justify-content: center;
  position: fixed;
  top: 0;
  left: 0;
  z-index: 1000;
`;

const Form = styled.div`
  width: 30%;
  height: 50%;
  background: white;
  border: 10px;
  padding: 20px;
  outline: 3px solid black;
  margin: 70px auto;

  input {
    width: 30%;
    margin-bottom: 20px;
  }

  textarea {
    width: 90%;
    height: 200px;
  }

  button {
    padding: 5px;
    background: black;
    color: white;
    outline: none;
    cursor: pointer;
    margin-top: 10px;
    margin-right: 10px;
  }
`;

export default function FontLightBox({ setShowFontLightBox }) {
  const [fontContent, setFontContent] = useState('');
  const [fontID, setFontID] = useState('');

  const addFontElement = (fontID = '', fontContent = '') => {
    const element = sheet.object(`${fontID}`, {
      position: {
        x: 0,
        y: 0,
      },
      bgColor: types.string('#ffffff'),
    });

    const div = document.createElement('div');

    div.style.cssText = `
    position: absolute;
    width: 300px;
    height: 20px;
    background: white;
    outline: 3px solid black;
    z-index: 10000;
    padding: 20px;
  `;

    div.textContent = `${fontContent}`;
    div.id = `${fontID}`;

    const container = document.getElementById('item-container');

    if (container) {
      container.appendChild(div);
    }

    let style = {};

    element.onValuesChange((newValue) => {
      div.style.left = newValue.position.x + 'px';
      div.style.top = newValue.position.y + 'px';
      div.style.fontSize = newValue.size;
      div.style.color = newValue.color;
    });

    // FIXME: 這一段可能會有問題
    setBoxes((prevBoxes) => [
      ...prevBoxes,
      { fontContent, element: div, id: fontID, type: 'box', style: style },
    ]);
  };

  return (
    <Wrapper>
      <Form>
        <h2>新增 Font 元件 </h2>

        <span>物件ID (勿與之前的重複):</span>
        <input
          placeholder='中/英/數字皆可'
          value={fontID}
          onChange={(e) => setFontID(e.target.value)}
        />

        <textarea
          placeholder='文字內容'
          value={fontContent}
          onChange={(e) => setFontContent(e.target.value)}
        />

        <button
          onClick={() => {
            setShowFontLightBox(false);
            addFontElement(fontID, fontContent);
          }}
        >
          確定
        </button>

        <button
          onClick={() => {
            setShowFontLightBox(false);
          }}
        >
          取消
        </button>
      </Form>
    </Wrapper>
  );
}
