import studio from '@theatre/studio';
import { types } from '@theatre/core';
import { useState, useEffect } from 'react';
import Stage from '../components/stage';
import { initialConfig } from '../constants';
import styled from 'styled-components';

const CopyEmbedded = styled.button`
  position: fixed;
  bottom: 20px;
  left: 20px;
  cursor: pointer;
`;

export default function ScrollVideo({ project, sheet }) {
  studio.ui.hide();
  project.ready.then(() =>
    sheet.sequence.play({
      iterationCount: Infinity,
    })
  );

  const [savedData, setSavedData] = useState([]);

  useEffect(() => {
    const elements = localStorage.getItem('theatre-data');
    if (elements) {
      setSavedData(JSON.parse(elements));
    }
  }, []);

  useEffect(() => {
    if (savedData.length > 0) {
      //render font control menu
      savedData
        .filter((item) => item.type === 'font')
        .forEach((item) => {
          const menuConfig = sheet.object(item.id, {
            ...initialConfig.FONT,
            content: types.string(`${item.content}`),
          });
          const element = document.getElementById(item.id);

          menuConfig.onValuesChange((newValue) => {
            element.style.left = newValue.position.x + '%';
            element.style.top = newValue.position.y + '%';
            element.style.background = newValue.bgColor;
            element.style.color = newValue.font.color;
            element.style.opacity = newValue.opacity;
            element.style.padding = newValue.padding + 'px';
            element.style.display = newValue.visible ? 'block' : 'none';
            element.style.width = newValue.size.width + 'px';
            element.style.height = newValue.size.height + 'px';
            element.style.fontSize = newValue.font.size + 'px';
            element.style.borderRadius = newValue.border.radius + '%';
            element.style.border = `${newValue.border.size}px solid ${newValue.border.color}`;
          });
        });
    }
  }, [savedData]);

  // ----- 處理 embedded-code ---------------

  let embedCode = '';

  embedCode = buildEmbeddedCode(selectedPkg, mockData, webpackAssets);

  if (selectedPkg) {
    if (selectedScriptUrl === 'localhost') {
      embedCode = buildEmbeddedCode(selectedPkg, mockData, webpackAssets);
    } else if (selectedScriptUrl === 'cdn') {
      embedCode = ecg.buildEmbeddedCode(
        selectedPkg,
        mockData,
        ecg.webpackAssets
      );
    }
  }

  return (
    <>
      <Stage savedData={savedData} sheet={sheet} />
      <CopyEmbedded
        onClick={() => {
          alert('已複製 EmbeddedCode');
        }}
      >
        Copy Embedded
      </CopyEmbedded>
    </>
  );
}
