import studio from '@theatre/studio';
import { getProject, types } from '@theatre/core';
import { useLayoutEffect, useMemo, useState } from 'react';
import EmbeddedCode from './embedded';
import styled from 'styled-components';
// import EmbeddedBox from './components/embedded-box';

const AddBoxButton = styled.div`
  padding: 5px;
  background: black;
  color: white;
  position: fixed;
  top: 0;
  left: 50%;
  cursor: pointer;
`;

export default function App() {
  studio.initialize();
  const project = getProject('deepfake demo');
  const sheet = project.sheet('Scene', 'default');

  const box = sheet.object('box', {
    position: {
      x: 0,
      y: 0,
    },
  });

  const addBox = () => {
    const div = document.createElement('div');
    console.log('有執行addBox function');

    div.style.cssText = `
      position: absolute;
      width: 100px;
      height: 100px;
      background: #EEE;
    `;

    document.body.appendChild(div);

    box.onValuesChange((newValue) => {
      div.style.left = newValue.position.x + 'px';
      div.style.top = newValue.position.y + 'px';
    });
  };

  return (
    <div>
      <EmbeddedCode />
      <Buttons>
        <AddBoxButton onClick={() => addBox()}>新增一個文字框</AddBoxButton>
      </Buttons>
    </div>
  );
}
