import studio from '@theatre/studio';
import { getProject, types } from '@theatre/core';
import { useState, useEffect } from 'react';
import projectState from '../data/state.json';
import Stage from '../components/stage';
import { initialConfig } from '../constants';
import ControlPanel from '../components/control-panel';

export default function Playground({
  project,
  sheet,
  setAnimateJSON,
  animateJSON,
}) {
  studio.ui.restore(); //顯示操作面板
  project.ready.then(() => sheet.sequence.pause);

  // ---------------------------------------------------------------

  // const [previousData, setPreviousData] = useState([]);
  // const [newData, setNewData] = useState([]);
  // const totalData = [...previousData, ...newData];

  const [eleData, setEleData] = useState([]);

  // //get local-storage previous data
  // useEffect(() => {
  //   // 沒有 previousData 的話，跟 local-storage 要
  //   if (previousData.length !== 0) {
  //     const elements = localStorage.getItem('theatre-data');
  //     if (elements) {
  //       setPreviousData(JSON.parse(elements));
  //     }
  //   }
  // }, []);

  useEffect(() => {
    if (eleData.length > 0) {
      //render font control menu
      eleData
        .filter((item) => item.type === 'font')
        .forEach((item) => {
          const menuConfig = sheet.object(item.id, initialConfig.FONT);
          const element = document.getElementById(item.id);

          menuConfig.onValuesChange((newValue) => {
            element.style.left = newValue.position.x + '%';
            element.style.top = newValue.position.y + '%';
            element.style.background = newValue.bgColor;
            element.style.color = newValue.font.color;
            element.style.opacity = newValue.opacity;
            element.style.padding = newValue.padding + 'px';
            element.style.display = newValue.visible ? 'block' : 'none';
            element.style.width = newValue.size.width + 'px';
            element.style.height = newValue.size.height + 'px';
            element.style.fontSize = newValue.font.size + 'px';
            element.style.borderRadius = newValue.border.radius + '%';
            element.style.border = `${newValue.border.size}px solid ${newValue.border.color}`;
          });
        });
    }

    //render image control menu
    // previousData
    //   .filter((item) => item.type === 'image')
    //   .forEach((item) => {
    //     const controlMenu = sheet.object(item.id, initialConfig.IMAGE);
    //     const element = document.getElementById(item.id);

    //     controlMenu.onValuesChange((newValue) => {
    //       element.style.left = newValue.position.x + 'px';
    //       element.style.top = newValue.position.y + 'px';
    //       element.style.backgroundColor = newValue.bgColor;
    //     });
    //   });
  }, [previousData]);

  // ---------------------------------------------------------------

  return (
    <>
      <ControlPanel
        setPreviousData={setPreviousData}
        previousData={previousData}
        totalData={totalData}
        sheet={sheet}
        setAnimateJSON={setAnimateJSON}
        animateJSON={animateJSON}
        setNewData={setNewData}
      />
      <Stage previousData={previousData} sheet={sheet} />
    </>
  );
}
