import React, { useState, useEffect } from 'react';
import styled from 'styled-components';

const Wrapper = styled.div`
  width: 100%;
  height: 100vh;
  background: black;
  opacity: 1;
  display: flex;
  justify-content: center;
  align-items: center;
  position: fixed;
  top: 0;
  left: 0;
  z-index: 10005;
`;

const Form = styled.div`
  width: 50%;
  background: white;
  border: 10px;
  padding: 10px 30px 30px;
  outline: 3px solid black;

  textarea {
    width: 100%;
    height: 200px;
    margin-bottom: 10px;
  }

  button {
    padding: 5px;
    background: black;
    color: white;
    outline: none;
    cursor: pointer;
    margin-top: 10px;
    margin-right: 10px;
  }
`;

export default function JSONLightBox({
  setShowJSONLightBox,
  setElementData,
  elementData,
  setAnimateJSON,
  animateJSON,
}) {
  const [elementDataInput, setElementDataInput] = useState(
    JSON.stringify(elementData)
  );
  const [animateJSONInput, setAnimateJSONInput] = useState(animateJSON);

  const isEmptyObject = (obj) => {
    return Object.keys(obj).length === 0;
  };

  // 輸入的資料要是 JSON
  // 但跑 map 的資料，不能是 json
  return (
    <Wrapper>
      <Form>
        <h2>新增/修改 JSON 資料</h2>

        <textarea
          placeholder='元件JSON'
          value={elementDataInput}
          onChange={(e) => setElementDataInput(e.target.value)}
        />

        <textarea
          placeholder='動態JSON'
          value={isEmptyObject(animateJSONInput) ? '' : animateJSONInput}
          onChange={(e) => setAnimateJSONInput(e.target.value)}
        />

        {/* <button
          onClick={() => {
            setShowJSONLightBox(false);
            setPreviousData(JSON.parse(elementDataInput));
            setAnimateJSON(animateJSONInput);
          }}
        >
          確認
        </button> */}

        <button
          onClick={() => {
            setShowJSONLightBox(false);
            setAnimateJSON({});

            const elements = localStorage.getItem('theatre-data');
            if (elements) {
              setElementData(JSON.parse(elements));
            }
          }}
        >
          skip，use default project
        </button>

        <button
          onClick={() => {
            setShowJSONLightBox(false);
          }}
        >
          取消
        </button>
      </Form>
    </Wrapper>
  );
}
