  IPlaybackDirection,
  IPlaybackRange,
} from '@theatre/core/sequences/Sequence'
import {defer} from '@theatre/shared/utils/defer'
import {InvalidArgumentError} from '@theatre/shared/utils/errors'
import noop from '@theatre/shared/utils/noop'
import type {Prism, Pointer, Ticker} from '@theatre/dataverse'
import {Atom} from '@theatre/dataverse'
import type {
  IPlaybackController,
  IPlaybackState,
} from './DefaultPlaybackController'
import {notify} from '@theatre/shared/notify'

export default class AudioPlaybackController implements IPlaybackController {
  _mainGain: GainNode
  private _state: Atom<IPlaybackState> = new Atom<IPlaybackState>({
    position: 0,
    playing: false,
  })
  readonly statePointer: Pointer<IPlaybackState>
  _stopPlayCallback: () => void = noop

  constructor(
    private readonly _decodedBuffer: AudioBuffer,
    private readonly _audioContext: AudioContext,
    private readonly _nodeDestination: AudioNode,
  ) {
    this.statePointer = this._state.pointer

    this._mainGain = this._audioContext.createGain()
    this._mainGain.connect(this._nodeDestination)
  }

  playDynamicRange(
    rangeD: Prism<IPlaybackRange>,
    ticker: Ticker,
  ): Promise<unknown> {
    const deferred = defer<boolean>()
    if (this._playing) this.pause()

    this._playing = true

    let stop: undefined | (() => void) = undefined

    const play = () => {
      stop?.()
      stop = this._loopInRange(rangeD.getValue(), ticker).stop
    }

    // We're keeping the rangeD hot, so we can read from it on every tick without
    // causing unnecessary recalculations
    const untapFromRangeD = rangeD.onStale(play)
    play()

    this._stopPlayCallback = () => {
      stop?.()
      untapFromRangeD()
      deferred.resolve(false)
    }

    return deferred.promise
  }

  private _loopInRange(
    range: IPlaybackRange,
    ticker: Ticker,
  ): {stop: () => void} {
    const rate = 1
    let startPos = this.getCurrentPosition()
    const iterationLength = range[1] - range[0]

    if (startPos < range[0] || startPos > range[1]) {
      // if we're currently out of the range
      this._updatePositionInState(range[0])
    } else if (startPos === range[1]) {
      // if we're currently at the very end of the range
      this._updatePositionInState(range[0])
    }
    startPos = this.getCurrentPosition()

    const currentSource = this._audioContext.createBufferSource()
    currentSource.buffer = this._decodedBuffer
    currentSource.connect(this._mainGain)
    currentSource.playbackRate.value = rate

    currentSource.loop = true
    currentSource.loopStart = range[0]
    currentSource.loopEnd = range[1]

    const initialTickerTime = ticker.time
    let initialElapsedPos = startPos - range[0]

    currentSource.start(0, startPos)

    const tick = (currentTickerTime: number) => {
      const elapsedTickerTime = Math.max(
        currentTickerTime - initialTickerTime,
        0,
      )
      const elapsedTickerTimeInSeconds = elapsedTickerTime / 1000

      const elapsedPos = elapsedTickerTimeInSeconds * rate + initialElapsedPos

      let currentIterationPos =
        ((elapsedPos / iterationLength) % 1) * iterationLength

      this._updatePositionInState(currentIterationPos + range[0])
      requestNextTick()
    }

    const requestNextTick = () => ticker.onNextTick(tick)
    ticker.onThisOrNextTick(tick)

    const stop = () => {
      currentSource.stop()
      currentSource.disconnect()
      ticker.offThisOrNextTick(tick)
      ticker.offNextTick(tick)
    }

    return {stop}
  }

  private get _playing() {
    return this._state.get().playing
  }

  private set _playing(playing: boolean) {
    this._state.setByPointer((p) => p.playing, playing)
  }

  destroy() {}

  pause() {
    this._stopPlayCallback()
    this._playing = false
    this._stopPlayCallback = noop
  }

  gotoPosition(time: number) {
    this._updatePositionInState(time)
  }

  private _updatePositionInState(time: number) {
    this._state.reduce((s) => ({...s, position: time}))
  }

  getCurrentPosition() {
    return this._state.get().position
  }

  play(
    iterationCount: number,
    range: IPlaybackRange,
    rate: number,
    direction: IPlaybackDirection,
    ticker: Ticker,
  ): Promise<boolean> {
    if (this._playing) {
      this.pause()
    }

    this._playing = true

    let startPos = this.getCurrentPosition()
    const iterationLength = range[1] - range[0]

    if (direction !== 'normal') {
      throw new InvalidArgumentError(
        `Audio-controlled sequences can only be played in the "normal" direction. ` +
          `'${direction}' given.`,
      )
    }

    if (startPos < range[0] || startPos > range[1]) {
      // if we're currently out of the range
      this._updatePositionInState(range[0])
    } else if (startPos === range[1]) {
      // if we're currently at the very end of the range
      this._updatePositionInState(range[0])
    }
    startPos = this.getCurrentPosition()

    const deferred = defer<boolean>()

    const currentSource = this._audioContext.createBufferSource()
    currentSource.buffer = this._decodedBuffer
    currentSource.connect(this._mainGain)
    currentSource.playbackRate.value = rate

    if (iterationCount > 1000) {
      notify.warning(
        "Can't play sequences with audio more than 1000 times",
        `The sequence will still play, but only 1000 times. The \`iterationCount: ${iterationCount}\` provided to \`sequence.play()\`
is too high for a sequence with audio.

To fix this, either set \`iterationCount\` to a lower value, or remove the audio from the sequence.`,
        [
          {
            url: 'https://www.theatrejs.com/docs/latest/manual/audio',
            title: 'Using Audio',
          },
          {
            url: 'https://www.theatrejs.com/docs/latest/api/core#sequence.attachaudio',
            title: 'Audio API',
          },
        ],
      )
      iterationCount = 1000
    }

    if (iterationCount > 1) {
      currentSource.loop = true
      currentSource.loopStart = range[0]
      currentSource.loopEnd = range[1]
    }

    const initialTickerTime = ticker.time
    let initialElapsedPos = startPos - range[0]
    const totalPlaybackLength = iterationLength * iterationCount

    currentSource.start(0, startPos, totalPlaybackLength - initialElapsedPos)

    const tick = (currentTickerTime: number) => {
      const elapsedTickerTime = Math.max(
        currentTickerTime - initialTickerTime,
        0,
      )
      const elapsedTickerTimeInSeconds = elapsedTickerTime / 1000

      const elapsedPos = Math.min(
        elapsedTickerTimeInSeconds * rate + initialElapsedPos,
        totalPlaybackLength,
      )

      if (elapsedPos !== totalPlaybackLength) {
        let currentIterationPos =
          ((elapsedPos / iterationLength) % 1) * iterationLength

        this._updatePositionInState(currentIterationPos + range[0])
        requestNextTick()
      } else {
        this._updatePositionInState(range[1])
        this._playing = false
        cleanup()
        deferred.resolve(true)
      }
    }

    const cleanup = () => {
      currentSource.stop()
      currentSource.disconnect()
    }

    this._stopPlayCallback = () => {
      cleanup()
      ticker.offThisOrNextTick(tick)
      ticker.offNextTick(tick)

      if (this._playing) deferred.resolve(false)
    }
    const requestNextTick = () => ticker.onNextTick(tick)
    ticker.onThisOrNextTick(tick)
    return deferred.promise
  }
}
/**
 * The names of the global variables that the core or studio bundle
 * use to store their references.
 */
export const studioBundle = '__TheatreJS_StudioBundle'
export const coreBundle = '__TheatreJS_CoreBundle'
export const notifications = '__TheatreJS_Notifications'
import logger from './logger'
import * as globalVariableNames from './globalVariableNames'

export type Notification = {title: string; message: string}
export type NotificationType = 'info' | 'success' | 'warning' | 'error'
export type Notify = (
  /**
   * The title of the notification.
   */
  title: string,
  /**
   * The message of the notification.
   */
  message: string,
  /**
   * An array of doc pages to link to.
   */
  docs?: {url: string; title: string}[],
  /**
   * Whether duplicate notifications should be allowed.
   */
  allowDuplicates?: boolean,
) => void
export type Notifiers = {
  /**
   * Show a success notification.
   */
  success: Notify
  /**
   * Show a warning notification.
   *
   * Say what happened in the title.
   * In the message, start with 1) a reassurance, then 2) explain why it happened, and 3) what the user can do about it.
   */
  warning: Notify
  /**
   * Show an info notification.
   */
  info: Notify
  /**
   * Show an error notification.
   */
  error: Notify
}

const createHandler =
  (type: NotificationType): Notify =>
  (...args) => {
    switch (type) {
      case 'success': {
        logger.debug(args.slice(0, 2).join('\n'))
        break
      }
      case 'info': {
        logger.debug(args.slice(0, 2).join('\n'))
        break
      }
      case 'warning': {
        logger.warn(args.slice(0, 2).join('\n'))
        break
      }
      case 'error': {
        // don't log errors, they're already logged by the browser
      }
    }

    return typeof window !== 'undefined'
      ? // @ts-ignore
        window[globalVariableNames.notifications]?.notify[type](...args)
      : undefined
  }

export const notify: Notifiers = {
  warning: createHandler('warning'),
  success: createHandler('success'),
  info: createHandler('info'),
  error: createHandler('error'),
}

if (typeof window !== 'undefined') {
  window.addEventListener('error', (e) => {
    notify.error(
      `An error occurred`,
      `<pre>${e.message}</pre>\n\nSee **console** for details.`,
    )
  })

  window.addEventListener('unhandledrejection', (e) => {
    notify.error(
      `An error occurred`,
      `<pre>${e.reason}</pre>\n\nSee **console** for details.`,
    )
  })
}
import {Ticker} from '@theatre/dataverse'
import {setPrivateAPI} from './privateAPIs'

export interface IRafDriver {
  /**
   * All raf derivers have have `driver.type === 'Theatre_RafDriver_PublicAPI'`
   */
  readonly type: 'Theatre_RafDriver_PublicAPI'
  /**
   * The name of the driver. This is used for debugging purposes.
   */
  name: string
  /**
   * The id of the driver. This is used for debugging purposes.
   * It's guaranteed to be unique.
   */
  id: number
  /**
   * This is called by the driver when it's time to tick forward.
   * The time param is of the same type returned by `performance.now()`.
   */
  tick: (time: number) => void
}

export interface RafDriverPrivateAPI {
  readonly type: 'Theatre_RafDriver_PrivateAPI'
  publicApi: IRafDriver
  ticker: Ticker
  start?: () => void
  stop?: () => void
}

let lastDriverId = 0

/**
 * Creates a custom raf driver.
 * `rafDriver`s allow you to control when and how often computations in Theatre tick forward. (raf stands for [`requestAnimationFrame`](https://developer.mozilla.org/en-US/docs/Web/API/window/requestAnimationFrame)).
 * The default `rafDriver` in Theatre creates a `raf` loop and ticks forward on each frame. You can create your own `rafDriver`, which enables the following use-cases:
 *
 * 1. When using Theatre.js alongside other animation libs (`@react-three/fiber`/`gsap`/`lenis`/`etc`), you'd want all animation libs to use a single `raf` loop to keep the libraries in sync and also to get better performance.
 * 2. In XR sessions, you'd want Theatre to tick forward using [`xr.requestAnimationFrame()`](https://developer.mozilla.org/en-US/docs/Web/API/XRSession/requestAnimationFrame).
 * 3. In some advanced cases, you'd just want to manually tick forward (many ticks per frame, or skipping many frames, etc). This is useful for recording an animation, rendering to a file, testing an animation, running benchmarks, etc.
 *
 * Here is how you'd create a custom `rafDriver`:
 *
 * ```js
 * import { createRafDriver } from '@theatre/core'
 *
 * const rafDriver = createRafDriver({ name: 'a custom 5fps raf driver' })
 *
 * setInterval(() => {
 *   rafDriver.tick(performance.now())
 * }, 200)
 * ```
 *
 * Now, any time you set up an `onChange()` listener, pass your custom `rafDriver`:
 *
 * ```js
 * import { onChange } from '@theatre/core'
 *
 * onChange(
 *   // let's say object is a Theatre object, the one returned from calling `sheet.object()`
 *   object.props,
 *   // this callback will now only be called at 5fps (and won't be called if there are no new values)
 *   // even if `sequence.play()` updates `object.props` at 60fps, this listener is called a maximum of 5fps
 *   (propValues) => {
 *     console.log(propValues)
 *   },
 *   rafDriver,
 * )
 *
 * // this will update the values of `object.props` at 60fps, but the listener above will still get called a maximum of 5fps
 * sheet.sequence.play()
 *
 * // we can also customize at what resolution the sequence's playhead moves forward
 * sheet.sequence.play({ rafDriver }) // the playhead will move forward at 5fps
 * ```
 *
 * You can optionally make studio use this `rafDriver`. This means the parts of the studio that tick based on raf, will now tick at 5fps. This is only useful if you're doing something crazy like running the studio (and not the core) in an XR frame.
 *
 * ```js
 * studio.initialize({
 *   __experimental_rafDriver: rafDriver,
 * })
 * ```
 *
 * `rafDriver`s can optionally provide a `start/stop` callback. Theatre will call `start()` when it actually has computations scheduled, and will call `stop` if there is nothing to update after a few ticks:
 *
 * ```js
 * import { createRafDriver } from '@theatre/core'
 * import type { IRafDriver } from '@theare/core'
 *
 * function createBasicRafDriver(): IRafDriver {
 *   let rafId: number | null = null
 *   const start = (): void => {
 *     if (typeof window !== 'undefined') {
 *       const onAnimationFrame = (t: number) => {
 *         driver.tick(t)
 *         rafId = window.requestAnimationFrame(onAnimationFrame)
 *       }
 *       rafId = window.requestAnimationFrame(onAnimationFrame)
 *     } else {
 *       driver.tick(0)
 *       setTimeout(() => driver.tick(1), 0)
 *     }
 *   }
 *
 *   const stop = (): void => {
 *     if (typeof window !== 'undefined') {
 *       if (rafId !== null) {
 *         window.cancelAnimationFrame(rafId)
 *       }
 *     } else {
 *       // nothing to do in SSR
 *     }
 *   }
 *
 *   const driver = createRafDriver({ name: 'DefaultCoreRafDriver', start, stop })
 *
 *   return driver
 * }
 * ```
 */
export function createRafDriver(conf?: {
  name?: string
  start?: () => void
  stop?: () => void
}): IRafDriver {
  const tick = (time: number): void => {
    ticker.tick(time)
  }

  const ticker = new Ticker({
    onActive() {
      conf?.start?.()
    },
    onDormant() {
      conf?.stop?.()
    },
  })

  const driverPublicApi: IRafDriver = {
    tick,
    id: lastDriverId++,
    name: conf?.name ?? `CustomRafDriver-${lastDriverId}`,
    type: 'Theatre_RafDriver_PublicAPI',
  }

  const driverPrivateApi: RafDriverPrivateAPI = {
    type: 'Theatre_RafDriver_PrivateAPI',
    publicApi: driverPublicApi,
    ticker,
    start: conf?.start,
    stop: conf?.stop,
  }

  setPrivateAPI(driverPublicApi, driverPrivateApi)

  return driverPublicApi
}
import type {Ticker} from '@theatre/dataverse'
import {privateAPI} from './privateAPIs'
import type {IRafDriver, RafDriverPrivateAPI} from './rafDrivers'
import {createRafDriver} from './rafDrivers'

/**
 * Creates a rafDrive that uses `window.requestAnimationFrame` in browsers,
 * or a single `setTimeout` in SSR.
 */
function createBasicRafDriver(): IRafDriver {
  let rafId: number | null = null
  const start = (): void => {
    if (typeof window !== 'undefined') {
      const onAnimationFrame = (t: number) => {
        driver.tick(t)
        rafId = window.requestAnimationFrame(onAnimationFrame)
      }
      rafId = window.requestAnimationFrame(onAnimationFrame)
    } else {
      driver.tick(0)
      setTimeout(() => driver.tick(1), 0)
    }
  }

  const stop = (): void => {
    if (typeof window !== 'undefined') {
      if (rafId !== null) {
        window.cancelAnimationFrame(rafId)
      }
    } else {
      // nothing to do in SSR
    }
  }

  const driver = createRafDriver({name: 'DefaultCoreRafDriver', start, stop})

  return driver
}

let coreRafDriver: RafDriverPrivateAPI | undefined

/**
 * Returns the rafDriver that is used by the core internally. Creates a new one if it's not set yet.
 */
export function getCoreRafDriver(): RafDriverPrivateAPI {
  if (!coreRafDriver) {
    setCoreRafDriver(createBasicRafDriver())
  }
  return coreRafDriver!
}

/**
 *
 * @returns The ticker that is used by the core internally.
 */
export function getCoreTicker(): Ticker {
  return getCoreRafDriver().ticker
}

/**
 * Sets the rafDriver that is used by the core internally.
 */
export function setCoreRafDriver(driver: IRafDriver) {
  if (coreRafDriver) {
    throw new Error(`\`setCoreRafDriver()\` is already called.`)
  }
  const driverPrivateApi = privateAPI(driver)
  coreRafDriver = driverPrivateApi
}
