import type {Studio} from '@theatre/studio/Studio'
import delay from '@theatre/shared/utils/delay'
import {original} from 'immer'
import type Project from './Project'
import type {OnDiskState} from './store/storeTypes'
import globals from '@theatre/shared/globals'

/**
 * @remarks
 * TODO this could be turned into a simple prism, like:
 * `editor.isReady: Prism<{isReady: true} | {isReady: false, reason: 'conflictBetweenDiskStateAndBrowserState'}>`
 */
export default async function initialiseProjectState(
  studio: Studio,
  project: Project,
  onDiskState: OnDiskState | undefined,
) {
  /*
   * If in the future we move to IndexedDB to store the state, we'll have
   * to deal with it being async (as opposed to localStorage that is synchronous.)
   * so here we're artifically delaying the loading of the state to make sure users
   * don't count on the state always being already loaded synchronously
   */
  await delay(0)

  studio.transaction(({drafts}) => {
    const projectId = project.address.projectId

    drafts.ephemeral.coreByProject[projectId] = {
      lastExportedObject: null,
      loadingState: {type: 'loading'},
    }

    drafts.ahistoric.coreByProject[projectId] = {
      ahistoricStuff: '',
    }

    function useInitialState() {
      drafts.ephemeral.coreByProject[projectId].loadingState = {
        type: 'loaded',
      }

      drafts.historic.coreByProject[projectId] = {
        sheetsById: {},
        definitionVersion: globals.currentProjectStateDefinitionVersion,
        revisionHistory: [],
      }
    }

    function useOnDiskState(state: OnDiskState) {
      drafts.ephemeral.coreByProject[projectId].loadingState = {
        type: 'loaded',
      }

      drafts.historic.coreByProject[projectId] = state
    }

    function useBrowserState() {
      drafts.ephemeral.coreByProject[projectId].loadingState = {
        type: 'loaded',
      }
    }

    function browserStateIsNotBasedOnDiskState(onDiskState: OnDiskState) {
      drafts.ephemeral.coreByProject[projectId].loadingState = {
        type: 'browserStateIsNotBasedOnDiskState',
        onDiskState,
      }
    }

    const browserState = original(drafts.historic)?.coreByProject[
      project.address.projectId
    ]

    if (!browserState) {
      if (!onDiskState) {
        useInitialState()
      } else {
        useOnDiskState(onDiskState)
      }
    } else {
      if (!onDiskState) {
        useBrowserState()
      } else {
        if (
          browserState.revisionHistory.indexOf(
            onDiskState.revisionHistory[0],
          ) == -1
        ) {
          browserStateIsNotBasedOnDiskState(onDiskState)
        } else {
          useBrowserState()
        }
      }
    }
  })
}
import type {
  ITheatreLoggerConfig,
  ITheatreLoggingConfig,
} from '@theatre/shared/logger'
import {TheatreLoggerLevel} from '@theatre/shared/logger'
import {createTheatreInternalLogger} from '@theatre/shared/logger'

export type CoreLoggingConfig = Partial<{
  logger: ITheatreLoggerConfig
  logging: ITheatreLoggingConfig
}>

function noop() {}

export function _coreLogger(config?: CoreLoggingConfig) {
  const internalMin = config?.logging?.internal
    ? config.logging.min ?? TheatreLoggerLevel.WARN
    : Infinity // if not internal, then don't show any logs
  const shouldDebugLogger = internalMin <= TheatreLoggerLevel.DEBUG
  const shouldShowLoggerErrors = internalMin <= TheatreLoggerLevel.ERROR
  const internal = createTheatreInternalLogger(undefined, {
    _debug: shouldDebugLogger
      ? console.debug.bind(console, '_coreLogger(TheatreInternalLogger) debug')
      : noop,
    _error: shouldShowLoggerErrors
      ? console.error.bind(console, '_coreLogger(TheatreInternalLogger) error')
      : noop,
  })

  if (config) {
    const {logger, logging} = config
    if (logger) internal.configureLogger(logger)
    if (logging) internal.configureLogging(logging)
    else {
      // default to showing Theatre.js dev logs in non-production environments
      internal.configureLogging({
        dev: process.env.NODE_ENV !== 'production',
      })
    }
  }

  return internal.getLogger().named('Theatre')
}
import userReadableTypeOfValue from '@theatre/shared/utils/userReadableTypeOfValue'
import {InvalidArgumentError} from '@theatre/shared/utils/errors'

const _validateSym = (
  val: string,
  thingy: string, // there are two unsolved problems in computer science: cache invalidation and naming things.
  range: [min: number, max: number],
): void | string => {
  if (typeof val !== 'string') {
    return `${thingy} must be a string. ${userReadableTypeOfValue(val)} given.`
  } else if (val.trim().length !== val.length) {
    return `${thingy} must not have leading or trailing spaces. '${val}' given.`
  } else if (val.length < range[0] || val.length > range[1]) {
    return `${thingy} must have between ${range[0]} and ${range[1]} characters. '${val}' given.`
  }
}

/**
 * Validates a name, so that:
 * - It's a string
 * - It doesn't have leading or trailing spaces
 * - It's between 3 and 32 characters long
 */
export const validateName = (
  name: string,
  thingy: string,
  shouldThrow: boolean = false,
) => {
  const result = _validateSym(name, thingy, [3, 32])
  if (typeof result === 'string' && shouldThrow) {
    throw new InvalidArgumentError(result)
  } else {
    return result
  }
}

/**
 * Validates an instanceId, so that:
 * - It's a string
 * - It doesn't have leading or trailing spaces
 * - It's between 1 and 32 characters long
 */
export const validateInstanceId = (
  name: string,
  thingy: string,
  shouldThrow: boolean = false,
) => {
  const result = _validateSym(name, thingy, [1, 32])
  if (typeof result === 'string' && shouldThrow) {
    throw new InvalidArgumentError(result)
  } else {
    return result
  }
}
