import React from 'react';
import styled from 'styled-components';
import FontElement from './elements/font';
import ImageElement from './elements/image';
import BackgroundElement from './elements/background';

const Wrapper = styled.div`
  width: 100%;
  height: 100vh;
  position: relative;
  overflow: hidden;
  margin: auto;
`;

export default function Stage({ objData, sheet, stageWidth, draggable }) {
  const fontElements = objData
    .filter((data) => data.type === 'FONT')
    .map((data) => {
      return (
        <FontElement
          key={'font' + data.id}
          id={data.id}
          sheet={sheet}
          stageWidth={stageWidth}
          draggable={draggable}
        />
      );
    });

  const imageElements = objData
    .filter((data) => data.type === 'IMAGE')
    .map((data) => {
      return (
        <ImageElement
          key={'image' + data.id}
          id={data.id}
          sheet={sheet}
          stageWidth={stageWidth}
          draggable={draggable}
        />
      );
    });

  const bgElements = objData
    .filter((data) => data.type === 'BACKGROUND')
    .map((data) => {
      return (
        <BackgroundElement
          key={'background' + data.id}
          id={data.id}
          sheet={sheet}
          stageWidth={stageWidth}
          draggable={draggable}
        />
      );
    });

  return (
    <Wrapper id='theatre-stage'>
      {fontElements}
      {imageElements}
      {bgElements}
    </Wrapper>
  );
}
