import React from 'react';
import styled from 'styled-components';

const GridContainer = styled.div`
  width: 100vw;
  height: 100vh;
  background-image: linear-gradient(to right, transparent 99px, black 1px),
    linear-gradient(to bottom, transparent 99px, black 1px);
  background-size: 100px 100px;
  background-repeat: repeat;
`;

const GridLine = styled.div`
  position: absolute;
  width: 100%;
  height: 1px;
  background-color: black;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-image: linear-gradient(
      to right,
      transparent 99px,
      black 1px
    ); /* 每 100px 绘制一条黑线 */
    background-size: 100px 100%; /* 100px 间隔重复绘制 */
  }

  .grid-vertical::before {
    background-image: linear-gradient(
      to bottom,
      transparent 99px,
      black 1px
    ); /* 每 100px 绘制一条黑线 */
    background-size: 100% 100px; /* 100px 间隔重复绘制 */
  }
`;

export default function GuideLine() {
  return (
    <GridContainer>
      {/* <GridLine />
      <GridLine className='grid-vertical' /> */}
    </GridContainer>
  );
}
