import { types } from '@theatre/core';

export const initialProperties = {
  FONT: {
    position: types.compound({
      x: types.number(0),
      y: types.number(0),
    }),
    color: types.compound({
      bg: types.rgba({ r: 255, g: 0, b: 0, a: 1 }),
      border: types.rgba({ r: 255, g: 0, b: 0, a: 1 }),
      font: types.rgba({ r: 255, g: 0, b: 0, a: 1 }),
    }),
    size_px: 0,
    title: types.string('這邊應該要代入內容'),
    content: types.string('這邊應該要代入標題'),
    opacity: types.number(0, {
      range: [0, 100],
    }),
    radius: types.string(''),
  },

  IMAGE: {
    position: {
      x: 0,
      y: 0,
    },
    bgColor: types.string('#ffffff'),
  },
};
