import studio from '@theatre/studio';
import { getProject, val } from '@theatre/core';
import { useState, useEffect, useRef } from 'react';
import styled from 'styled-components';
import TheatreElements from '../components/theatre-elements';

// import { getProject, types } from '@theatre/core';
import projectState from '../json/state.json';

const defaultViewportHeight = '100vh';

const ViewBox = styled.div`
  position: sticky;
  width: 100%;
  height: 100vh;
  overflow: hidden;
`;

const TheatreContainer = styled.div`
  width: 100%;
  height: 100%;
  outline: 5px solid red;
  overflow-y: auto;
  padding-right: 15px; //to hide scroll-bar
`;

const ScrollHeight = styled.div`
  background: pink;
  width: 100%;
  height: 200vh;
`;

// 下面參考 scrollable-video ---------------------------------

const PositionStarter = styled.div`
  width: 0;
  height: 0;
  opacity: 0;
`;

const PositionFinisher = styled.div`
  width: 0;
  height: 0;
  opacity: 0;
`;

const Other = styled.div`
  width: 100%;
  height: 100vh;
`;

export default function ScrollDemo() {
  // ------------------------------------------------

  studio.initialize();
  const project = getProject('deepfake demo', { state: projectState });
  const sheet = project.sheet('Scene', 'default');

  studio.ui.hide();
  project.ready.then(() => sheet.sequence.pause);

  // ------------------------------------------------

  const starterRef = useRef(null);
  const finisherRef = useRef(null);
  const theatreRef = useRef(null);

  //滑動的離頂端有多少距離
  const [innerScrollTop, setInnerScrollTop] = useState(0);

  console.log('innerScrollTop', innerScrollTop);

  useEffect(() => {
    const container = theatreRef.current;

    const handleInnerScroll = () => {
      const currentPosition = container.scrollTop;
      setInnerScrollTop(currentPosition);
    };

    container.addEventListener('inner-scroll', handleInnerScroll);

    return () => {
      container.removeEventListener('inner-scroll', handleInnerScroll);
    };
  }, []);

  const [scrollEnabled, setScrollEnabled] = useState(false);

  const sequenceLength = val(sheet.sequence.pointer.length);

  // 透過 scrollPosition 去決定 sequence 的位置
  useEffect(() => {
    // 2000 等同於 ScrollHeight 元件的高度
    const newPosition = (innerScrollTop / 2000) * sequenceLength;
    sheet.sequence.position = newPosition;
  }, [innerScrollTop]);

  const handleScrollEnable = () => {
    const starterRect = starterRef.current.getBoundingClientRect();
    const isAtTop = starterRect.top <= 0;

    console.log('starterRect.top', starterRect.top);
    console.log('isAtTop', isAtTop);

    setScrollEnabled(isAtTop);
  };

  useEffect(() => {
    const handleScroll = () => {
      handleScrollEnable();
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <>
      <Other />
      <PositionStarter ref={starterRef} id='starter' />
      <ViewBox
        style={{
          top: scrollEnabled ? '0' : 'auto',
          zIndex: scrollEnabled ? '1' : 'auto',
        }}
      >
        <TheatreContainer
          id='theatre-container'
          ref={theatreRef}
          style={{ overflow: scrollEnabled ? 'auto' : 'hidden' }}
        >
          <TheatreElements sheet={sheet} />
          <ScrollHeight />
        </TheatreContainer>
      </ViewBox>
      <PositionFinisher ref={finisherRef} id='finisher' />

      <Other />
    </>
  );
}
