import React from 'react';
import styled from 'styled-components';

const gridColor = '#29e2f3';

const Grid = styled.div`
  position: absolute;
  bottom: 0;
  left: 0;
  width: 100%;
  height: 100vh;
  margin: auto;

  background-image: repeating-linear-gradient(
      to right,
      ${gridColor} 0,
      ${gridColor} 0.5px,
      transparent 0.5px,
      transparent 49.5px
    ),
    repeating-linear-gradient(
      to right,
      ${gridColor} 0,
      ${gridColor} 0.5px,
      transparent 0.5px,
      transparent 99.5px
    ),
    repeating-linear-gradient(
      to bottom,
      ${gridColor} 0,
      ${gridColor} 0.5px,
      transparent 0.5px,
      transparent 49.5px
    ),
    repeating-linear-gradient(
      to bottom,
      ${gridColor} 0,
      ${gridColor} 0.5px,
      transparent 0.5px,
      transparent 99.5px
    );
  background-size: 100px 100px;
  background-repeat: repeat;
  background-position: 100% 100%;
  pointer-events: none;
  opacity: 0.5;

  /* 添加紅線 */
  &::before {
    content: '';
    position: absolute;
    top: 50%;
    left: 0;
    width: 100%;
    height: 2px;
    background-color: red;
    background-image: repeating-linear-gradient(
      to right,
      transparent 0,
      transparent ${dashWidth},
      red ${dashWidth},
      red ${parseInt(dashWidth) + parseInt(dashSpacing)}
    );
  }
`;

export default function GridLine() {
  return <Grid />;
}
