import React, { useState } from 'react';
import styled from 'styled-components';

const Wrapper = styled.div`
  width: 100%;
  height: 100vh;
  background: black;
  opacity: 1;
  display: flex;
  justify-content: center;
  position: fixed;
  top: 0;
  left: 0;
  z-index: 200;
`;

const Form = styled.div`
  width: 30%;
  height: 20%;
  background: white;
  border: 10px;
  padding: 20px;
  outline: 3px solid black;
  margin: 150px auto;

  input {
    width: 70%;
    margin-bottom: 20px;
  }

  button {
    padding: 5px;
    background: black;
    color: white;
    outline: none;
    cursor: pointer;
    margin-top: 10px;
    margin-right: 10px;
  }
`;

export default function AddVideo({
  setShowVideoBox,
  setObjData,
  objData,
  sheet,
}) {
  const [source, setSource] = useState('');

  // TODO: 多項刪除
  const AddVideo = (id) => {
    const filteredData = objData.filter((item) => item.id !== id);
    setObjData(filteredData);
    localStorage.setItem('theatre-data', JSON.stringify(filteredData));
  };

  const addVideoElement = () => {
    const lastVideoId = objData
      .filter((element) => element.type === 'VIDEO')
      .reduce((maxId, element) => {
        const idNumber = parseInt(element.id.split('-')[1]);
        return idNumber > maxId ? idNumber : maxId;
      }, 0);

    const nextVideoId = `video-${String(lastVideoId + 1).padStart(3, '0')}`;
    const updatedElementData = [
      ...objData,
      { id: nextVideoId, type: 'VIDEO', src: source },
    ];

    localStorage.setItem('theatre-data', JSON.stringify(updatedElementData));
    setObjData(updatedElementData);
  };

  return (
    <Wrapper>
      <Form>
        <h2>新增影片元件 </h2>

        <span> 影片來源 </span>
        <input
          placeholder=''
          value={deleteID}
          onChange={(e) => setSource(e.target.value)}
        />

        <button
          onClick={() => {
            addVideoElement();
            window.location.reload();
          }}
        >
          確定
        </button>

        <button
          onClick={() => {
            setShowVideoBox(false);
          }}
        >
          取消
        </button>
      </Form>
    </Wrapper>
  );
}
