import React, { useState, useEffect } from 'react';
import styled, { keyframes } from 'styled-components';
import FontElement from './elements/font';
import ImageElement from './elements/image';
import BackgroundElement from './elements/background';
import Dimmer from './dimmer-with-message';

const Wrapper = styled.div`
  width: 100%;
  height: 100vh;
  position: relative;
  overflow: hidden;
  margin: auto;
`;

const circle = keyframes`
  0% {
    transform: rotate(0deg);
  }

  100% {
    transform: rotate(360deg);
  }
`;

export default function Stage({ objData, sheet, stageWidth, draggable }) {
  const [loadedImages, setLoadedImages] = useState(0);
  const [showDimmer, setShowDimmer] = useState(true);

  const totalImages = objData.filter(
    (data) => data.type === 'IMAGE' || data.type === 'BACKGROUND'
  ).length;

  const handleImageLoad = () => {
    setLoadedImages((prev) => prev + 1);
  };

  console.log('showDimmer', showDimmer);
  console.log('loadedImages', loadedImages);
  console.log('totalImages', totalImages);

  useEffect(() => {
    if (loadedImages === totalImages) {
      setShowDimmer(false);
    }
  }, [loadedImages, totalImages]);

  // let loadingJsx = null;

  // if (showDimmer) {
  //   loadingJsx = (
  //     <Dimmer show={showDimmer} message='捲動式影片載入中' shining={true} />
  //   );
  // }

  const loadingJsx = (
    <Dimmer show={showDimmer} message='載入中' shining={true} />
  );

  // ----------------------------------------

  const fontElements = objData
    .filter((data) => data.type === 'FONT')
    .map((data) => {
      return (
        <FontElement
          key={'font' + data.id}
          id={data.id}
          sheet={sheet}
          stageWidth={stageWidth}
          draggable={draggable}
        />
      );
    });

  const imageElements = objData
    .filter((data) => data.type === 'IMAGE')
    .map((data) => {
      return (
        <ImageElement
          key={'image' + data.id}
          id={data.id}
          sheet={sheet}
          stageWidth={stageWidth}
          draggable={draggable}
          onLoad={handleImageLoad}
        />
      );
    });

  const bgElements = objData
    .filter((data) => data.type === 'BACKGROUND')
    .map((data) => {
      return (
        <BackgroundElement
          key={'background' + data.id}
          id={data.id}
          sheet={sheet}
          stageWidth={stageWidth}
          draggable={draggable}
          onLoad={handleImageLoad}
        />
      );
    });

  // 我需要讓 showDimmer = true 時，不可以捲動
  return (
    <>
      {loadingJsx}
      <Wrapper id='theatre-stage'>
        {fontElements}
        {imageElements}
        {bgElements}
      </Wrapper>
    </>
  );
}
