import studio from '@theatre/studio';
import { getProject, types } from '@theatre/core';
import { useState, useEffect } from 'react';
import styled from 'styled-components';
import { onChange, val } from '@theatre/core';
import Stage from '../components/stage';

const Container = styled.div`
  width: 100%;
  height: 100vh;
  position: relative;
  outline: 5px solid red;
  z-index: -1;
  overflow: hidden;
`;

const BasicBox = styled.div`
  position: absolute;
  width: 300px;
  height: 20px;
  background: white;
  outline: 3px solid black;
  z-index: 10000;
  padding: 20px;
`;

export default function DemoVideo() {
  studio.initialize();
  const project = getProject('Demo: Video', {});
  const sheet = project.sheet('Scene', 'default');
  studio.ui.hide();
  project.ready.then(() => sheet.sequence.play());

  // ------------------------------------------

  const [elementJSON, setElementJSON] = useState([]);
  const [animateData, setAnimateData] = useState([]);

  // get localStorage data
  useEffect(() => {
    const elements = localStorage.getItem('theatre-data');

    if (elements) {
      setElementJSON(JSON.parse(elements));
    }

    const animates = localStorage.getItem('theatre-0.4.persistent');
    if (animates) {
      setAnimateData(JSON.parse(animates));
    }
  }, []);

  return (
    <>
      <Container id='item-container'>
        {preData.map((box, index) => (
          <BasicBox key={index} id={box.id} style={box.style}>
            {box.content}
          </BasicBox>
        ))}
      </Container>

      <Stage sheet={sheet} savedData={elementJSON} />
    </>
  );
}
