import studio from '@theatre/studio';
import { getProject, types } from '@theatre/core';
import { useLayoutEffect, useMemo, useState } from 'react';
import EmbeddedCode from './embedded';
import styled from 'styled-components';
import state from './json/state.json';
import EmbeddedBox from './components/embedded-box';
import FontBox from './components/font-box';

const Buttons = styled.div`
  position: fixed;
  padding: 30px;
  bottom: 0;
  left: 0;
  background: pink;
  z-index: 10000;
`;

const AddButton = styled.div`
  padding: 5px;
  outline: 3px solid black;
  background: white;
  color: black;
  cursor: pointer;
  font-size: 14px;
  margin: 20px auto;

  &:hover {
    cursor: pointer;
  }
`;

export default function App() {
  studio.initialize();
  // const project = getProject('deepfake demo');

  const project = getProject('deepfake demo', { state });
  const sheet = project.sheet('Scene', 'default');

  const addBox = ({ name = '', content = '' }) => {
    console.log('有執行 addBox ');

    const box = sheet.object('box', {
      position: {
        x: 0,
        y: 0,
      },
    });

    const div = document.createElement('div');

    div.style.cssText = `
      position: absolute;
      width: 300px;
      height: 100px;
      background: white;
      outline: 3px solid black;
      z-index: 10000;
      padding: 20px;

    `;

    div.textContent = '文字內容';

    document.body.appendChild(div);

    box.onValuesChange((newValue) => {
      div.style.left = newValue.position.x + 'px';
      div.style.top = newValue.position.y + 'px';
    });
  };

  const addEmbedded = () => {
    const embedded = sheet.object('embedded', {
      position: {
        x: 0,
        y: 0,
      },
    });

    const div = document.createElement('div');
    console.log('有執行addEmbedded function');

    div.style.cssText = `
      position: absolute;
      width: 100%;
      height: 100vh;
      background: white;
    `;

    document.body.appendChild(div);

    embedded.onValuesChange((newValue) => {
      div.style.left = newValue.position.x + 'px';
      div.style.top = newValue.position.y + 'px';
    });
  };

  const [showEmBox, setShowEmBox] = useState(false);
  const [showFontBox, setShowFontBox] = useState(false);
  const [embed, setEmbed] = useState('');
  const [font, setFont] = useState('');
  const [boxName, setBoxName] = useState('box');

  return (
    <>
      {showEmBox && (
        <EmbeddedBox
          setShowEmBox={setShowEmBox}
          setEmbed={setEmbed}
          embed={embed}
        />
      )}

      {showFontBox && (
        <FontBox
          setShowFontBox={setShowFontBox}
          setFont={setFont}
          font={font}
          setBoxName={setBoxName}
          boxName={boxName}
          addBox={addBox}
        />
      )}

      <Buttons>
        <AddButton
          className='add-embedded-box'
          onClick={() => {
            setShowEmBox(true);
          }}
        >
          新增embedded
        </AddButton>
        <AddButton
          className='add-font-box'
          onClick={() => {
            // setShowFontBox(true);
            addBox('box1', '內容內容');
          }}
        >
          新增一個文字框
        </AddButton>
      </Buttons>
      <EmbeddedCode embed={embed} setEmbed={setEmbed} />
    </>
  );
}
