import React, { useState } from 'react';
import styled from 'styled-components';
import { initialConfig } from '../../constants';
import studio from '@theatre/studio';
import { types } from '@theatre/core';
import FontElement from '../element/font-element';
import { createRoot } from 'react-dom/client';

const Wrapper = styled.div`
  width: 100%;
  height: 100vh;
  background: black;
  opacity: 1;
  display: flex;
  justify-content: center;
  position: fixed;
  top: 0;
  left: 0;
  z-index: 10005;
`;

const Form = styled.div`
  width: 30%;
  height: 50%;
  background: white;
  border: 10px;
  padding: 20px;
  outline: 3px solid black;
  margin: 70px auto;

  input {
    width: 30%;
    margin-bottom: 20px;
  }

  textarea {
    width: 90%;
    height: 200px;
  }

  button {
    padding: 5px;
    background: black;
    color: white;
    outline: none;
    cursor: pointer;
    margin-top: 10px;
    margin-right: 10px;
  }
`;

export default function FontLightBox({
  setShowFontLightBox,

  sheet,
}) {
  const [fontContent, setFontContent] = useState('');
  const [fontID, setFontID] = useState('');

  const addFontElement = (fontID = '', fontContent = '') => {
    const menuConfig = sheet.object(`${fontID}`, {
      ...initialConfig.FONT,
      content: types.string(`${fontContent}`),
    });

    const [fontStyle, setFontStyle] = useState({});

    const fontElement = (
      <FontElement
        id={fontID}
        content={fontContent}
        onClick={() => studio.setSelection([menuConfig])}
        style={fontStyle}
      />
    );

    const container = document.getElementById('theatre-stage');
    if (container) {
      createRoot(container).render(fontElement);
    }

    menuConfig.onValuesChange((newValue) => {
      setFontStyle({
        left: `${newValue.position.x}%`,
        top: `${newValue.position.y}%`,
        background: newValue.bgColor,
        color: newValue.font.color,
        opacity: newValue.opacity,
        padding: `${newValue.padding}px`,
        display: newValue.visible ? 'block' : 'none',
        width: `${newValue.size.width}px`,
        height: `${newValue.size.height}px`,
        fontSize: `${newValue.font.size}px`,
        borderRadius: `${newValue.border.radius}%`,
        border: `${newValue.border.size}px solid ${newValue.border.color}`,
      });
    });

    // const updatedElementData = [
    //   ...elementData,
    //   { id: fontID, content: fontContent, type: 'font' },
    // ];
    // setElementData(updatedElementData);
    // localStorage.setItem('theatre-data', JSON.stringify(updatedElementData));
  };

  return (
    <Wrapper>
      <Form>
        <h2>新增 Font 元件 </h2>

        <span>物件 ID (勿與之前的重複)： </span>
        <input
          placeholder='中/英/數字皆可'
          value={fontID}
          onChange={(e) => setFontID(e.target.value)}
        />

        <textarea
          placeholder='文字內容'
          value={fontContent}
          onChange={(e) => setFontContent(e.target.value)}
        />

        <button
          onClick={() => {
            setShowFontLightBox(false);
            addFontElement(fontID, fontContent);
          }}
        >
          確定
        </button>

        <button
          onClick={() => {
            setShowFontLightBox(false);
          }}
        >
          取消
        </button>
      </Form>
    </Wrapper>
  );
}
