import studio from '@theatre/studio';
import { getProject, types } from '@theatre/core';
import { useLayoutEffect, useMemo, useState } from 'react';
import EmbeddedCode from './embedded';
import styled from 'styled-components';
import state from './json/state.json';

const Buttons = styled.div`
  position: fixed;
  padding: 30px;
  bottom: 0;
  left: 0;
  background: pink;
  z-index: 10000;
`;

const AddButton = styled.div`
  padding: 5px;
  outline: 3px solid black;
  background: white;
  color: black;
  cursor: pointer;
  font-size: 14px;
  margin: 20px auto;

  &:hover {
    cursor: pointer;
  }
`;

export default function App() {
  studio.initialize();
  // const project = getProject('deepfake demo');

  const project = getProject('deepfake demo', { state });
  const sheet = project.sheet('Scene', 'default');

  const box1 = sheet.object('box1', {
    position: {
      x: 0,
      y: 0,
    },
  });

  const embedded = sheet.object('box2', {
    position: {
      x: 0,
      y: 0,
    },
  });

  const addBox = () => {
    const div = document.createElement('div');
    console.log('有執行addBox function');

    div.style.cssText = `
      position: absolute;
      width: 300px;
      height: 100px;
      background: white;
      outline: 3px solid black;
      z-index: 10000;
    `;

    document.body.appendChild(div);

    box1.onValuesChange((newValue) => {
      div.style.left = newValue.position.x + 'px';
      div.style.top = newValue.position.y + 'px';
    });
  };

  const addEmbedded = () => {
    const div = document.createElement('div');
    console.log('有執行addEmbedded function');

    div.style.cssText = `
      position: absolute;
      width: 100%;
      height: 100vh;
      background: white;
    `;

    document.body.appendChild(div);

    embedded.onValuesChange((newValue) => {
      div.style.left = newValue.position.x + 'px';
      div.style.top = newValue.position.y + 'px';
    });
  };

  return (
    <>
      <Buttons>
        <AddButton
          className='add-font-box'
          onClick={() => {
            console.log('有點擊到');
            addBox();
          }}
        >
          新增一個文字框
        </AddButton>

        <AddButton
          className='add-embedded-box'
          onClick={() => {
            console.log('有點擊到');
          }}
        >
          新增一個文字框
        </AddButton>
      </Buttons>
      <EmbeddedCode />
    </>
  );
}
