import studio from '@theatre/studio';
import { val, getProject } from '@theatre/core';
import { useState, useEffect, useRef } from 'react';
import styled from 'styled-components';
import Stage from '../components/stage';
import Dimmer from '../components/dimmer-with-message';

const ViewBox = styled.div`
  position: relative;
  width: 100vw;
  min-width: 100vw;
  max-width: 100vw;
  min-height: 100vh;
  background: #ffffff;
`;

const StageWrapper = styled.div`
  width: 100%;
  height: 100vh;
  position: sticky;
  top: 0;
  left: 0;
`;

const defaultScrollScale = 1500;

export default function DemoScroll() {
  studio.initialize();
  studio.ui.hide();
  const project = getProject('Project', {});
  const sheet = project.sheet('Scene', 'default');
  project.ready.then(() => sheet.sequence.pause());

  // --------------------------
  // TODO: 增加條件，減少 event-listener 偵測時間

  const [scrollPosition, setScrollPosition] = useState(0);

  const sequenceLength = val(sheet.sequence.pointer.length); //播放總時間長度
  const stageRef = useRef(null);

  useEffect(() => {
    const handleScroll = () => {
      const stage = stageRef.current;
      const viewbox = stage.closest('.theatre-scroll-viewbox');

      const stageRect = stage.getBoundingClientRect();
      const viewboxRect = viewbox.getBoundingClientRect();

      const distance = stageRect.top - viewboxRect.top;
      setScrollPosition(distance);
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  useEffect(() => {
    const newPosition =
      (scrollPosition / (sequenceLength * defaultScrollScale)) * sequenceLength;
    sheet.sequence.position = newPosition;
  }, [scrollPosition]);

  const objJson = localStorage.getItem('theatre-data'); // get objectJSON in local-storage
  const objData = objJson ? JSON.parse(objJson) : [];

  const screenWidth = window.innerWidth;

  // ----------------------------

  // 1. 處理單純內容 loading
  let loadingJsx = null;
  if (videoDuration === 0 && isVideoLoading) {
    loadingJsx = (
      <Dimmer show={isVideoLoading} message='捲動式影片載入中' shining />
    );
  } else if (isVideoLoading) {
    // 已經有影片，但影片正在跑
    loadingJsx = <Spinner />;
  }

  //   <Dimmer
  //   show={isVideoError}
  //   message={
  //     '捲動式影片影片載入失敗。請檢查您的網路連線，並重新整理瀏覽器。若您的瀏覽器不支援捲動式影片，請使用較新版 Firefox、Chrome、Safari、或 Edge 瀏覽器觀看。'
  //   }
  // />
  // <Dimmer
  //   show={isScrollTriggerError}
  //   message={
  //     '您的瀏覽器不支援捲動式影片，請使用較新版 Firefox、Chrome、Safari、或 Edge 瀏覽器觀看。'
  //   }
  // />

  return (
    <ViewBox
      className='theatre-scroll-viewbox'
      style={{ height: `${sequenceLength * defaultScrollScale}px` }}
    >
      <StageWrapper ref={stageRef}>
        {loadingJsx}
        <Stage
          objData={objData}
          sheet={sheet}
          stageWidth={screenWidth}
          draggable={false}
        />
      </StageWrapper>
    </ViewBox>
  );
}
