import React from 'react';
import studio from '@theatre/studio';
import styled from 'styled-components';
import { Link } from 'react-router-dom';
import { getLocalStorageData } from '../../utils';

const Wrapper = styled.div`
  padding: 5px;
  background: rgba(255, 255, 255, 0.95);
  max-width: 160px;
  border-radius: 10px;
  margin: 10px 0 10px 10px;
  outline: 2px solid #e9e9e9;

  button {
    padding: 5px;
    margin: 5px;
    cursor: pointer;
    background: #e9e9e9;
    border: none;
    border-radius: 5px;
  }
`;

const buttons = [
  {
    label: 'play',
    onClick: ({ sheet }) => {
      sheet.sequence.play({
        iterationCount: Infinity,
      });
    },
  },
  {
    label: 'pause',
    onClick: ({ sheet }) => {
      sheet.sequence.pause();
    },
  },
  {
    label: '開啟介面',
    onClick: () => {
      studio.ui.restore();
    },
  },
  {
    label: '隱藏介面',
    onClick: () => {
      studio.ui.hide();
    },
  },
  {
    label: 'show grid',
    onClick: ({ setShowGrid }) => {
      setShowGrid(true);
    },
  },
  {
    label: 'hide grid',
    onClick: ({ setShowGrid }) => {
      setShowGrid(false);
    },
  },
  {
    label: 'clear ALL data',
    onClick: () => {
      const confirmation = confirm('清空 project 資料 (localStorage)');

      if (confirmation) {
        // 用户点击了 "确定" 按钮
        // 在这里执行清空 localStorage 的步骤
        localStorage.clear(); // 或者其他清空 localStorage 的逻辑
      } else {
        // 用户点击了 "取消" 按钮
        // 可以在这里执行一些其他操作或者什么都不做
      }

      localStorage.removeItem('theatre-data');
      localStorage.removeItem('theatre-0.4.persistent');
      alert('清空 project 資料 (localStorage)');
      window.location.reload();
    },
  },

  {
    label: 'show JSON data',
    onClick: ({ setShowJSONLightBox, setElementJSON, setAnimateJSON }) => {
      const animate = localStorage.getItem('theatre-0.4.persistent');
      const animateState =
        JSON.parse(animate)?.historic?.innerState?.coreByProject?.Project ?? {};

      setAnimateJSON(JSON.stringify(animateState));
      setElementJSON(localStorage.getItem('theatre-data'));
      setShowJSONLightBox(true);
    },
  },
];

export default function SettingButtons({
  sheet,
  setShowJSONLightBox,
  setElementJSON,
  setAnimateJSON,
  setShowGrid,
}) {
  return (
    <Wrapper>
      {buttons.map((button, index) => (
        <button
          key={index}
          onClick={() =>
            button.onClick({
              sheet,
              setShowJSONLightBox,
              setElementJSON,
              setAnimateJSON,
              setShowGrid,
            })
          }
        >
          {button.label}
        </button>
      ))}

      <Link to='/demo-scroll' target='_blank'>
        <button>Scroll</button>
      </Link>

      <Link to='/demo-video' target='_blank'>
        <button>Video</button>
      </Link>
    </Wrapper>
  );
}
