import { useState, useEffect } from 'react';
import styled from 'styled-components';
import { getProject } from '@theatre/core';
import {
  defaultFontStyle,
  defaultImageStyle,
} from '../components/shared-style';
import { renderFontObject, renderImageObject } from '../utils';

const Wrapper = styled.div`
  width: 100%;
  height: 100vh;
  position: relative;
`;

const FontElement = styled.div`
  ${defaultFontStyle}
`;

const ImageElement = styled.div`
  ${defaultImageStyle}
`;

export default function DemoVideo() {
  const project = getProject('Project', {});
  const sheet = project.sheet('Scene', 'default');

  project.ready.then(() =>
    sheet.sequence.play({
      iterationCount: Infinity,
    })
  );

  // ---------------------------

  const [storedData, setStoredData] = useState([]); // get data in local-storage

  useEffect(() => {
    const elements = localStorage.getItem('theatre-data');
    if (elements) {
      setStoredData(JSON.parse(elements));
    }
  }, []);

  useEffect(() => {
    if (storedData.length > 0) {
      renderFontObject(storedData, sheet);
      renderImageObject(storedData, sheet);
    }
  }, [storedData]);

  // ---------------------------

  const fontElements = storedData
    .filter((data) => data.type === 'FONT')
    .map((data, index) => {
      return (
        <FontElement key={index} id={data.id}>
          {data.content}
        </FontElement>
      );
    });

  const imageElements = storedData
    .filter((data) => data.type === 'IMAGE')
    .map((data, index) => {
      return (
        <ImageElement
          key={index}
          id={data.id}
          style={{ backgroundImage: `url(${data.url})` }}
        />
      );
    });

  return (
    <Wrapper id='theatre-stage'>
      {fontElements}
      {imageElements}
    </Wrapper>
  );
}
