import React from 'react';
import styled from 'styled-components';
import {
  defaultFontStyle,
  defaultImageStyle,
  defaultBgStyle,
} from '../shared-style/index';
import studio from '@theatre/studio'; //這個 demo 版本要刪掉'
import defaultImage from '../assets/default-image.png';

const Wrapper = styled.div`
  width: 100vw;
  min-width: 100vw;
  max-width: 100vw;
  height: 100vh;
  position: relative;
  overflow: hidden;

  /* RWD 用 */
  /* width: 50%;
  max-width: 768px;
  margin: auto;
  outline: 5px solid red; */
`;

const FontElement = styled.div`
  ${defaultFontStyle}
`;

const ImageElement = styled.div`
  ${defaultImageStyle}
  background-image: url('../assets/default-image.png');
  /* background-image: url(${(props) => (props.url ? props.url : '/')}); */
`;

const BgElement = styled.div`
  ${defaultBgStyle}
  background-image: url(${(props) => props.url});
`;

export default function Stage({ storedData }) {
  const fontElements = storedData
    .filter((data) => data.type === 'FONT')
    .map((data, index) => {
      return (
        <FontElement key={index} id={data.id}>
          {/* {data.content} */}
        </FontElement>
      );
    });

  const imageElements = storedData
    .filter((data) => data.type === 'IMAGE')
    .map((data, index) => {
      // return <ImageElement key={index} id={data.id} url={data.url} />;
      return <ImageElement key={index} id={data.id} />;
    });

  const bgElements = storedData
    .filter((data) => data.type === 'BACKGROUND')
    .map((data, index) => {
      return <BgElement key={index} id={data.id} url={data.url} />;
    });

  return (
    <Wrapper id='theatre-stage'>
      {fontElements}
      {imageElements}
      {bgElements}
    </Wrapper>
  );
}
