import React from 'react';
import styled from 'styled-components';
import {
  defaultFontStyle,
  defaultImageStyle,
  defaultBgStyle,
} from '../shared-style/index';

const Wrapper = styled.div`
  width: 100vw;
  /* min-width: 100vw; */
  /* max-width: 100vw; */
  height: 100vh;
  position: relative;
  overflow: hidden;

  /* RWD 用 */

  width: 50%;

  margin: auto;
  outline: 1px solid red;
`;

const FontElement = styled.div`
  ${defaultFontStyle}
`;

const ImageElement = styled.div`
  ${defaultImageStyle};
`;

const BgElement = styled.div`
  ${defaultBgStyle};
`;

export default function Stage({ objData, stageSize }) {
  const fontElements = objData
    .filter((data) => data.type === 'FONT')
    .map((data, index) => {
      return <FontElement key={index} id={data.id} />;
    });

  const imageElements = objData
    .filter((data) => data.type === 'IMAGE')
    .map((data, index) => {
      return (
        <ImageElement key={index} id={data.id}>
          <img src='/default-image.png' alt={data.id} />
        </ImageElement>
      );
    });

  const bgElements = objData
    .filter((data) => data.type === 'BACKGROUND')
    .map((data, index) => {
      return (
        <BgElement key={index} id={data.id}>
          <img src='/default-image.png' alt={data.id} />
        </BgElement>
      );
    });

  return (
    <Wrapper id='theatre-stage' style={{ width: stageSize + 'px' }}>
      {fontElements}
      {imageElements}
      {bgElements}
    </Wrapper>
  );
}
