import React, { useState, useEffect } from 'react';
import styled, { keyframes } from 'styled-components';
import FontElement from './elements/font';
import ImageElement from './elements/image';
import BackgroundElement from './elements/background';
import Dimmer from './dimmer-with-message';

const Wrapper = styled.div`
  width: 100%;
  height: 100vh;
  position: relative;
  overflow: hidden;
  margin: auto;
`;

const circle = keyframes`
  0% {
    transform: rotate(0deg);
  }

  100% {
    transform: rotate(360deg);
  }
`;

const SpinnerBlock = styled.div`
  position: absolute;
  left: 50%;
  top: 50%;
  transform: translate(-50%, -50%);
  width: 5%;

  & > svg {
    animation: 1s ${circle} linear infinite;
  }

  @media screen and (max-width: 767px) {
    width: 10%;
  }
`;

function Spinner() {
  return (
    <SpinnerBlock>
      <svg
        viewBox='0 0 20 20'
        version='1.1'
        xmlns='http://www.w3.org/2000/svg'
        xmlnsXlink='http://www.w3.org/1999/xlink'
      >
        <g stroke='none' strokeWidth='1' fill='none' fillRule='evenodd'>
          <g fill='#fff' fillRule='nonzero'>
            <path d='M10,3.5 C6.41015,3.5 3.5,6.41015 3.5,10 C3.5,10.4142 3.16421,10.75 2.75,10.75 C2.33579,10.75 2,10.4142 2,10 C2,5.58172 5.58172,2 10,2 C14.4183,2 18,5.58172 18,10 C18,14.4183 14.4183,18 10,18 C9.58579,18 9.25,17.6642 9.25,17.25 C9.25,16.8358 9.58579,16.5 10,16.5 C13.5899,16.5 16.5,13.5899 16.5,10 C16.5,6.41015 13.5899,3.5 10,3.5 Z'></path>
          </g>
        </g>
      </svg>
    </SpinnerBlock>
  );
}

export default function Stage({ objData, sheet, stageWidth, draggable }) {
  const [loadedImages, setLoadedImages] = useState(0);
  const [showDimmer, setShowDimmer] = useState(true);

  const totalImages = objData.filter((data) => data.type === 'IMAGE').length;

  const handleImageLoad = () => {
    setLoadedImages((prev) => prev + 1);
  };

  console.log('showDimmer', showDimmer);

  useEffect(() => {
    if (loadedImages === totalImages) {
      setShowDimmer(false);
    }
  }, [loadedImages, totalImages]);

  let loadingJsx = null;

  if (showDimmer) {
    loadingJsx = (
      <Dimmer show={showDimmer} message='捲動式影片載入中' shining={true} />
    );
  }

  // ----------------------------------------

  const fontElements = objData
    .filter((data) => data.type === 'FONT')
    .map((data) => {
      return (
        <FontElement
          key={'font' + data.id}
          id={data.id}
          sheet={sheet}
          stageWidth={stageWidth}
          draggable={draggable}
        />
      );
    });

  const imageElements = objData
    .filter((data) => data.type === 'IMAGE')
    .map((data) => {
      return (
        <ImageElement
          key={'image' + data.id}
          id={data.id}
          sheet={sheet}
          stageWidth={stageWidth}
          draggable={draggable}
          onLoad={handleImageLoad}
        />
      );
    });

  const bgElements = objData
    .filter((data) => data.type === 'BACKGROUND')
    .map((data) => {
      return (
        <BackgroundElement
          key={'background' + data.id}
          id={data.id}
          sheet={sheet}
          stageWidth={stageWidth}
          draggable={draggable}
        />
      );
    });

  return (
    <>
      {loadingJsx}
      <Wrapper id='theatre-stage'>
        {fontElements}
        {imageElements}
        {bgElements}
      </Wrapper>
    </>
  );
}
