import React from 'react';
import styled from 'styled-components';
// import { initialConfig } from '../constants';
import studio from '@theatre/studio';
import { defaultFontStyle, defaultImageStyle } from './shared-style/index';

const Wrapper = styled.div`
  width: 100%;
  height: 100vh;
  position: relative;
`;

const FontElement = styled.div`
  position: absolute;
  display: flex;
  align-items: center;
  justify-content: center;
  word-wrap: break-word;
  transform-origin: center;
`;

const ImageElement = styled.div`
  position: absolute;
  display: flex;
  align-items: center;
  justify-content: center;
  background-size: cover;
  background-repeat: no-repeat;
  background-position: center;
`;

// 如何讓透過 local-storage 裡 render 的資料可以被選取？
// 注意：但純 render，要被轉換成 embedded 的不需要
export default function PlaygroundStage({ storedData, sheet }) {
  const fontElements = storedData
    .filter((data) => data.type === 'font')
    .map((data, index) => {
      return (
        <FontElement
          key={index}
          id={data.id}
          onClick={() => {
            const obj = sheet.object(data.id, initialConfig.FONT);
            studio.setSelection([obj]);
          }}
        >
          {data.content}
        </FontElement>
      );
    });

  const imageElements = storedData
    .filter((data) => data.type === 'image')
    .map((data, index) => {
      const style = { backgroundImage: `url(${data.url})` };
      return <ImageElement key={index} id={data.id} style={style} />;
    });

  return (
    <Wrapper id='theatre-stage'>
      {fontElements}
      {imageElements}
    </Wrapper>
  );
}
