import React, { useState } from 'react';
import ElementButtons from './button/element-button';
import SettingButtons from './button/setting-button';
import FontLightBox from './lightbox/font-lightbox';
import JSONLightBox from './lightbox/json-lightbox';
import styled from 'styled-components';

const LightBoxWrapper = styled.div``;

const ButtonWrapper = styled.div`
  position: fixed;
  bottom: 0;
  left: 0;
  display: flex;
  justify-content: space-between;
  z-index: 10000;
`;

export default function ControlPanel({
  setElementData,
  elementData,
  sheet,
  setAnimateJSON,
  animateJSON,
}) {
  const [showFontLightBox, setShowFontLightBox] = useState(false);
  const [showJSONLightBox, setShowJSONLightBox] = useState(false);

  return (
    <>
      <LightBoxWrapper>
        {showFontLightBox && (
          <FontLightBox
            setShowFontLightBox={setShowFontLightBox}
            setElementData={setElementData}
            sheet={sheet}
          />
        )}

        {showJSONLightBox && (
          <JSONLightBox
            setShowJSONLightBox={setShowJSONLightBox}
            sheet={sheet}
            setElementData={setElementData}
            elementData={elementData}
            setAnimateJSON={setAnimateJSON}
            animateJSON={animateJSON}
          />
        )}
      </LightBoxWrapper>

      <ButtonWrapper>
        <SettingButtons
          sheet={sheet}
          totalData={totalData}
          setShowJSONLightBox={setShowJSONLightBox}
        />
        <ElementButtons setShowFontLightBox={setShowFontLightBox} />
      </ButtonWrapper>
    </>
  );
}
