import React, { useState } from 'react';
import styled from 'styled-components';
import { initialConfig } from '../../constants';
import studio from '@theatre/studio';
import { types } from '@theatre/core';

const Wrapper = styled.div`
  width: 100%;
  height: 100vh;
  background: black;
  opacity: 1;
  display: flex;
  justify-content: center;
  position: fixed;
  top: 0;
  left: 0;
  z-index: 200;
`;

const Form = styled.div`
  width: 30%;
  height: 50%;
  background: white;
  border: 10px;
  padding: 20px;
  outline: 3px solid black;
  margin: 70px auto;

  input {
    width: 30%;
    margin-bottom: 20px;
  }

  textarea {
    width: 90%;
    height: 200px;
  }

  button {
    padding: 5px;
    background: black;
    color: white;
    outline: none;
    cursor: pointer;
    margin-top: 10px;
    margin-right: 10px;
  }
`;

export default function ImageLightBox({ setShowImageLightBox, sheet }) {
  const [imageURL, setImageUrl] = useState('');
  const [imageID, setImageID] = useState('');

  const addImageElement = (id = '', url = '') => {
    const menuConfig = sheet.object(`${id}`, {
      ...initialConfig.IMAGE,
      url: types.string(`${url}`),
    });

    const div = document.createElement('div');

    div.id = `${id}`;
    div.onclick = () => studio.setSelection([menuConfig]);
    div.style.backgroundImage = `url(${imageURL})`;
    div.style.cssText = `
      position: absolute;
      display: flex;
      align-items: center;
      justify-content: center;
      background-size: cover;
      background-repeat: no-repeat;
      background-position: center;
    `;

    const container = document.getElementById('theatre-stage');
    if (container) {
      container.appendChild(div);
    }

    menuConfig.onValuesChange((newValue) => {
      div.style.left = newValue.position.x + '%';
      div.style.top = newValue.position.y + '%';
      div.style.opacity = newValue.opacity;
      div.style.display = newValue.visible ? 'block' : 'none';
      div.style.width = newValue.size.width + 'px';
      div.style.height = newValue.size.height + 'px';
      div.style.borderRadius = newValue.border.radius + '%';
      div.style.border = `${newValue.border.size}px solid ${newValue.border.color}`;
      div.style.backgroundImage = `url(${newValue.url})`;
      setImageUrl(newValue.url);
    });

    const theatreData = JSON.parse(localStorage.getItem('theatre-data')) || [];
    const updatedElementData = [
      ...theatreData,
      { id: id, url: url, type: 'image' },
    ];
    localStorage.setItem('theatre-data', JSON.stringify(updatedElementData));
  };

  return (
    <Wrapper>
      <Form>
        <h2>新增 Image 元件 </h2>

        <span>物件 ID (勿與之前的重複)： </span>
        <input
          placeholder='中/英/數字皆可'
          value={imageID}
          onChange={(e) => setImageID(e.target.value)}
        />

        <textarea
          placeholder='圖片連結'
          value={imageURL}
          onChange={(e) => setImageUrl(e.target.value)}
        />

        <button
          onClick={() => {
            setShowImageLightBox(false);
            addImageElement(imageID, imageURL);
          }}
        >
          確定
        </button>

        <button
          onClick={() => {
            setShowImageLightBox(false);
          }}
        >
          取消
        </button>
      </Form>
    </Wrapper>
  );
}
