import React, { useState } from 'react';
import styled from 'styled-components';

const Wrapper = styled.div`
  width: 100%;
  height: 100vh;
  background: black;
  opacity: 1;
  display: flex;
  justify-content: center;
  align-items: center;
  position: fixed;
  top: 0;
  left: 0;
  z-index: 100;
`;

const Form = styled.div`
  background: white;
  border: 10px;
  padding: 10px 30px 30px;
  outline: 3px solid black;
  display: flex;

  textarea {
    width: 100%;
    height: 100px;
    margin-bottom: 10px;
  }

  button {
    padding: 10px;
    background: black;
    color: white;
    outline: none;
    cursor: pointer;
    margin-top: 10px;
    margin-right: 10px;
  }
`;

const Block = styled.div`
  margin: 0 30px;
  width: 400px;
`;

export default function JSONLightBox({
  setShowJSONLightBox,
  animateJSON,
  objData,
  setObjData,
  setState,
}) {
  const [inputObj, setInputObj] = useState('');
  const [inputAnimate, setInputAnimate] = useState('');

  // console.log('inputObj', inputObj);
  // console.log('parse inputObj', JSON.parse(inputObj)); //要透過 parse 轉換才能進行 map

  //當按下確定時，objData = JSON.parse(inputObj) OK
  //inputAnimate (1) 要被當成 state 被讀取 (2) 覆蓋掉 storage 資料，看哪個可行～

  // console.log('inputAnimate parse', JSON.parse(inputAnimate));

  //先測試一下，確定貼上 state + edit 是可以的

  console.log('objData', objData);

  return (
    <Wrapper>
      <Form>
        <Block>
          <h2> JSON 資料 (Read only) </h2>
          <p>元件 JSON</p>
          <textarea
            placeholder='元件JSON'
            value={JSON.stringify(objData)}
            readOnly={true}
          />

          <p>動畫 JSON</p>

          {/* 如果 animateJSON 沒有經過 JSON.stringify，那就會是 [object, object] */}
          <textarea
            placeholder='動畫JSON'
            value={JSON.stringify(animateJSON)}
            readOnly={true}
          />
        </Block>

        {/* 這是新的 block */}
        <Block>
          <h2> Edit JSON </h2>
          <p>元件 JSON</p>
          <textarea
            placeholder='貼上要讀取的元件JSON'
            value={inputObj}
            onChange={(e) => setInputObj(e.target.value)}
          />

          <p>動畫 JSON</p>

          {/* 如果 animateJSON 沒有經過 JSON.stringify，那就會是 [object, object] */}
          <textarea
            placeholder='貼上要讀取的動畫JSON'
            value={inputAnimate}
            onChange={(e) => setInputAnimate(e.target.value)}
          />

          <button
            onClick={() => {
              setShowJSONLightBox(false);
            }}
          >
            Close
          </button>

          <button
            onClick={() => {
              setShowJSONLightBox(false);

              //處理 inputObj 資料
              localStorage.setItem('theatre-data', inputObj);
              setObjData(JSON.parse(inputObj)); //把 render 資料換成 inputObj

              //在 window.reload 之後，state 會重回 {}
              localStorage.setItem('theatre-state', inputAnimate);
              // setState(JSON.stringify(inputAnimate)); // 會遇到衝突，要選 disk

              // window.location.reload();
            }}
          >
            儲存修改
          </button>
        </Block>

        {/* <textarea
          placeholder='動畫JSON'
          value={JSON.stringify(parsedAnimateJSON, null, 2)} // Pretty print JSON for display
          onChange={(e) => setAnimateJSON(e.target.value)} // Update the JSON directly as an object
        /> */}

        {/* <button
          onClick={() => {
            setShowJSONLightBox(false);
            localStorage.setItem('theatre-data', elementJSON);

            const oldAnimateData = localStorage.getItem(
              'theatre-0.4.persistent'
            );

            const updatedAnimateData = {
              ...oldAnimateData,
              historic: {
                innerState: {
                  coreByProject: {
                    Project: animateJSON,
                  },
                },
              },
            };

            localStorage.setItem('theatre-0.4.persistent', updatedAnimateData);

            window.location.reload();
          }}
        >
          儲存修改
        </button> */}
      </Form>
    </Wrapper>
  );
}
