import React from 'react';
import studio from '@theatre/studio';
import styled from 'styled-components';
import { Link } from 'react-router-dom';
import { getLocalStorageData } from '../../utils';

const Wrapper = styled.div`
  padding: 10px;
  background: #f4dac8;
  max-width: 180px;
  margin-right: 15px;

  button {
    padding: 5px;
    margin: 5px;
    cursor: pointer;
    background: white;
    border: 1px solid grey;
    border-radius: 5px;
  }
`;

const buttons = [
  {
    label: 'play',
    onClick: ({ sheet }) => {
      sheet.sequence.play({
        iterationCount: Infinity,
      });
    },
  },
  {
    label: 'pause',
    onClick: ({ sheet }) => {
      sheet.sequence.pause();
    },
  },
  {
    label: '隱藏介面',
    onClick: () => {
      studio.ui.hide();
    },
  },
  {
    label: '開啟介面',
    onClick: () => {
      studio.ui.restore();
    },
  },
  {
    label: 'clear ALL data',
    onClick: () => {
      localStorage.removeItem('theatre-data');
      localStorage.removeItem('theatre-0.4.persistent');
      alert('清空 project 資料 (localStorage)');
      window.location.reload();
    },
  },

  {
    label: 'show JSON data',
    onClick: ({ setShowJSONLightBox, setElementJSON, setAnimateJSON }) => {
      setElementJSON(getLocalStorageData('theatre-data'));
      setAnimateJSON(getLocalStorageData('theatre-0.4.persistent'));
      setShowJSONLightBox(true);
    },
  },
];

export default function SettingButtons({
  sheet,
  setShowJSONLightBox,
  setElementJSON,
  setAnimateJSON,
}) {
  return (
    <Wrapper>
      {buttons.map((button, index) => (
        <button
          key={index}
          onClick={() =>
            button.onClick({
              sheet,
              setShowJSONLightBox,
              setElementJSON,
              setAnimateJSON,
            })
          }
        >
          {button.label}
        </button>
      ))}

      <Link to='/demo-scroll' target='_blank'>
        <button>預覽 Scroll 效果</button>
      </Link>

      <Link to='/demo-video' target='_blank'>
        <button>預覽 Video 效果</button>
      </Link>
    </Wrapper>
  );
}
