import React, { useState, useMemo } from 'react';
import styled from 'styled-components';
import {
  defaultFontStyle,
  defaultImageStyle,
  defaultBgStyle,
} from '../shared-style/index';
import studio from '@theatre/studio';

const Wrapper = styled.div`
  width: 100%;
  height: 100vh;
  position: relative;
  overflow: hidden;
  margin: auto;
`;

const FontElement = styled.div`
  ${defaultFontStyle}
`;

const ImageElement = styled.div`
  ${defaultImageStyle};
`;

const BgElement = styled.div`
  ${defaultBgStyle};
`;

export default function Stage({ objData, stageSize }) {
  // 處理拖拉功能
  const [divRef, setDivRef] = useState(null);

  const dragOpts = useMemo(() => {
    let scrub;
    let initial;
    let firstOnDragCalled = false;
    return {
      onDragStart() {
        scrub = studio.scrub();
        initial = obj.value;
        firstOnDragCalled = false;
      },
      onDrag(x, y) {
        if (!firstOnDragCalled) {
          studio.setSelection([obj]);
          firstOnDragCalled = true;
        }
        scrub.capture(({ set }) => {
          set(obj.props, { x: x + initial.x, y: y + initial.y });
        });
      },
      onDragEnd(dragHappened) {
        if (dragHappened) {
          scrub.commit();
        } else {
          scrub.discard();
        }
      },
      lockCursorTo: 'move',
    };
  }, []);

  useDrag(divRef, dragOpts);

  // --------------------------------

  const fontElements = objData
    .filter((data) => data.type === 'FONT')
    .map((data, index) => {
      return <FontElement key={index} id={data.id} ref={setDivRef} />;
    });

  const imageElements = objData
    .filter((data) => data.type === 'IMAGE')
    .map((data, index) => {
      return (
        <ImageElement key={index} id={data.id}>
          <img src='/default-image.png' alt={data.id} ref={setDivRef} />
        </ImageElement>
      );
    });

  const bgElements = objData
    .filter((data) => data.type === 'BACKGROUND')
    .map((data, index) => {
      return (
        <BgElement key={index} id={data.id}>
          <img src='/default-image.png' alt={data.id} ref={setDivRef} />
        </BgElement>
      );
    });

  // const stageWidth = stageSize ? stageSize + 'px' : '100vw';

  return (
    <Wrapper id='theatre-stage'>
      {fontElements}
      {imageElements}
      {bgElements}
    </Wrapper>
  );
}
