import studio from '@theatre/studio';
import { getProject, types } from '@theatre/core';
import { useState, useEffect } from 'react';
import EmbeddedCode from '../embedded';
import styled from 'styled-components';
import EmbeddedBox from '../components/embedded-box';
import FontBox from '../components/font-box';
import ReactDOM from 'react-dom';
import Font from '../components/font';
import state from '../json/state.json';

const Buttons = styled.div`
  position: fixed;
  padding: 30px;
  bottom: 0;
  left: 0;
  background: pink;
  z-index: 10000;
`;

const AddButton = styled.div`
  padding: 5px;
  outline: 3px solid black;
  background: white;
  color: black;
  cursor: pointer;
  font-size: 14px;
  margin: 20px auto;

  &:hover {
    cursor: pointer;
  }
`;

const Play = styled.div`
  width: 100px;
  height: 50px;
  background: white;

  &:hover {
    cursor: pointer;
  }
`;

const Container = styled.div`
  min-width: 100%;
  /* min-height: 100vh; */
  /* height: 50vh; */
  position: relative;
  outline: 5px solid red;
  z-index: -1;
`;

const Save = styled.button`
  padding: 5px;
  margin: 10px;

  &:hover {
    cursor: pointer;
  }
`;

export default function PlayPage() {
  studio.initialize();

  const config = {}; // the config can be empty when starting a new project
  const project = getProject('deepfake demo', config);
  const sheet = project.sheet('Scene', 'default');

  // const { projectId } = project.address; //project 名稱

  const [boxes, setBoxes] = useState([]);

  useEffect(() => {
    // 从本地存储中获取数据
    const storedBoxes = localStorage.getItem('boxes');
    if (storedBoxes) {
      setBoxes(JSON.parse(storedBoxes));
    }
  }, []);

  // useEffect(() => {
  //   // 从本地存储中获取数据
  //   const storedBoxes = localStorage.getItem('boxes');
  //   if (storedBoxes) {
  //     setBoxes(JSON.parse(storedBoxes));
  //   }
  // }, []);

  // useEffect(() => {
  //   // 当 `boxes` 发生变化时，将数据保存到本地存储中
  //   localStorage.setItem('boxes', JSON.stringify(boxes));
  // }, [boxes]);

  console.log('boxes', boxes);

  const addBox = (name = '', content = '') => {
    //新增一個控制項
    const box = sheet.object(`${name}`, {
      position: {
        x: 0,
        y: 0,
      },
      bgColor: types.string('#ffffff'),
    });

    //創建 div 至指定 container 中

    const div = document.createElement('div');

    div.style.cssText = `
    position: absolute;
    width: 300px;
    height: 20px;
    background: white;
    outline: 3px solid black;
    z-index: 10000;
    padding: 20px;
  `;

    div.textContent = `${content}`;

    const container = document.getElementById('item-container');

    if (container) {
      container.appendChild(div);
    }

    box.onValuesChange((newValue) => {
      div.style.left = newValue.position.x + 'px';
      div.style.top = newValue.position.y + 'px';
      div.style.backgroundColor = newValue.bgColor;
    });

    setBoxes((prevBoxes) => [
      ...prevBoxes,
      { id: name, content, element: div },
    ]);
  };

  const addFont = (name = '', content = '') => {
    const box = sheet.object(`${name}`, {
      position: {
        x: 0,
        y: 0,
      },
      size: types.string('50px'),
      color: types.string('#000000'),
    });

    // const div = document.createElement('div');

    // div.style.cssText = `
    //   font-size: 50px;
    //   position: absolute;
    //   color: black;
    //   z-index: 10000000;
    // `;

    div.textContent = `${content}`;

    document.body.appendChild(div);

    box.onValuesChange((newValue) => {
      div.style.left = newValue.position.x + 'px';
      div.style.top = newValue.position.y + 'px';
      div.style.fontSize = newValue.size;
      div.style.color = newValue.color;
    });
  };

  const addEmbedded = (embed, setEmbed) => {
    const embedded = sheet.object('embedded', {
      position: {
        x: 0,
        y: 0,
      },

      scale: types.number(1, {
        range: [0, 5],
        nudgeMultiplier: 0.1,
      }),
    });

    const div = document.createElement('div');

    div.style.cssText = `
      position: absolute;
      width: 100%;
      height: 100vh;
      background: white;
      transition: transform 0.3s ease;
      transform-origin: top left;
    `;

    document.body.appendChild(div);

    embedded.onValuesChange((newValue) => {
      div.style.left = newValue.position.x + 'px';
      div.style.top = newValue.position.y + 'px';
      div.style.transform = `scale(${newValue.scale})`;
    });

    ReactDOM.render(<EmbeddedCode embed={embed} setEmbed={setEmbed} />, div);
  };

  const [showEmBox, setShowEmBox] = useState(false);
  const [showFontBox, setShowFontBox] = useState(false);
  const [showFont, setShowFont] = useState(false);
  const [embed, setEmbed] = useState('');
  const [font, setFont] = useState('');
  const [pureFont, setPureFont] = useState('');
  const [boxName, setBoxName] = useState('box');
  const [pureFontName, setPureFontName] = useState('font');

  return (
    <>
      {showEmBox && (
        <EmbeddedBox
          setShowEmBox={setShowEmBox}
          setEmbed={setEmbed}
          embed={embed}
          addEmbedded={addEmbedded}
        />
      )}

      {showFontBox && (
        <FontBox
          setShowFontBox={setShowFontBox}
          setFont={setFont}
          font={font}
          setBoxName={setBoxName}
          boxName={boxName}
          addBox={addBox}
        />
      )}

      {showFont && (
        <Font
          setShowFont={setShowFont}
          setPureFont={setPureFont}
          pureFont={pureFont}
          setPureFontName={setPureFontName}
          pureFontName={pureFontName}
          addFont={addFont}
        />
      )}

      <Buttons>
        <Save
          onClick={() => {
            console.log('有存進 local-storage ');
            localStorage.setItem('boxes', JSON.stringify(boxes));
          }}
        >
          儲存
        </Save>
        <Play
          onClick={() => {
            sheet.sequence.play({
              iterationCount: Infinity,
            });
          }}
        >
          播放動畫
        </Play>
        <Play
          onClick={() => {
            sheet.sequence.pause();
          }}
        >
          暫停
        </Play>
        <AddButton
          className='add-embedded-box'
          onClick={() => {
            setShowEmBox(true);
          }}
        >
          新增embedded
        </AddButton>
        <AddButton
          className='add-font-box'
          onClick={() => {
            setShowFontBox(true);
          }}
        >
          新增文字框
        </AddButton>
        <AddButton
          className='add-font'
          onClick={() => {
            setShowFont(true);
          }}
        >
          新增文字標題
        </AddButton>
      </Buttons>

      <Container id='item-container'>
        {boxes.map((box, index) => (
          <div key={index} id={box.id}>
            {box.content}
          </div>
        ))}
      </Container>
    </>
  );
}
