import studio from '@theatre/studio';
import { val, getProject } from '@theatre/core';
import { useState, useEffect, useRef } from 'react';
import styled from 'styled-components';
import Stage from '../components/stage';
import Dimmer from '../components/dimmer-with-message';

const ScrollSizer = styled.div`
  position: relative;
  width: 100vw;
  min-width: 100vw;
  max-width: 100vw;
  min-height: 100vh;
  background: #ffffff;
`;

const StickyBox = styled.div`
  width: 100%;
  height: 100vh;
  position: sticky;
  top: 0;
  left: 0;
`;

const defaultScrollScale = 1500;

export default function DemoScroll() {
  studio.initialize();
  studio.ui.hide();
  const project = getProject('Project', {});
  const sheet = project.sheet('Scene', 'default');
  project.ready.then(() => sheet.sequence.pause());

  // --------------------------
  // TODO: 增加條件，減少 event-listener 偵測時間

  const [scrollPosition, setScrollPosition] = useState(0);

  const sequenceLength = val(sheet.sequence.pointer.length);
  const stageRef = useRef(null);

  useEffect(() => {
    const handleScroll = () => {
      const stage = stageRef.current;
      const sizer = stage.closest('.theatre-scroll-sizer');

      const stageRect = stage.getBoundingClientRect();
      const sizerRect = sizer.getBoundingClientRect();

      const distance = stageRect.top - sizerRect.top;
      setScrollPosition(distance);
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  useEffect(() => {
    const newPosition =
      (scrollPosition / (sequenceLength * defaultScrollScale)) * sequenceLength;
    sheet.sequence.position = newPosition;
  }, [scrollPosition]);

  // ----------------------------

  const objJson = localStorage.getItem('theatre-data'); // get objectJSON in local-storage
  const objData = objJson ? JSON.parse(objJson) : [];

  // ----------------------------

  const [hasImgError, setHasImgError] = useState(false); // image & background onError
  const [loadedImages, setLoadedImages] = useState(0); // image & background onload
  const [isLoading, setIsLoading] = useState(true);

  const totalImages = objData.filter(
    (data) => data.type === 'IMAGE' || data.type === 'BACKGROUND'
  ).length;

  useEffect(() => {
    if (loadedImages === totalImages) {
      setIsLoading(false);
    }
  }, [loadedImages, totalImages]);

  const sizerHeight = hasImgError
    ? '100vh'
    : `${sequenceLength * defaultScrollScale}px`;

  return (
    <ScrollSizer
      className='theatre-scroll-sizer'
      style={{ height: sizerHeight }}
    >
      <StickyBox ref={stageRef}>
        <Dimmer
          show={isLoading && !hasImgError}
          message={'載入中'}
          shining={true}
        />
        <Dimmer
          show={hasImgError}
          message={'載入失敗。請檢查您的網路連線，並重新整理瀏覽器。'}
        />
        <Stage
          objData={objData}
          sheet={sheet}
          stageWidth={window.innerWidth}
          draggable={false}
          setHasImgError={setHasImgError}
          setLoadedImages={setLoadedImages}
        />
      </StickyBox>
    </ScrollSizer>
  );
}
