import React from 'react';
import styled from 'styled-components';

const Wrapper = styled.div`
  padding: 10px;
  background: rgba(255, 255, 255, 0.95);
  max-width: 100px;
  border-radius: 10px;
  margin: 10px;
  outline: 2px solid #e9e9e9;

  button {
    padding: 5px;
    background: white;
    margin: 5px;
    cursor: pointer;
    background: #e9e9e9;
    border: none;
    border-radius: 5px;
  }
`;

const buttons = [
  {
    label: '新增 - 背景',
    className: 'add-background',
    onClick: ({ setShowBgLightBox }) => {
      setShowBgLightBox(true);
    },
  },
  {
    label: '新增 - 文字',
    className: 'add-font',
    onClick: () => {
      // setShowFontLightBox(true);
      addFontElement();
    },
  },
  {
    label: '新增 - 圖片',
    className: 'add-image',
    onClick: ({ setShowImageLightBox }) => {
      setShowImageLightBox(true);
    },
  },
  {
    label: '刪除元件',
    className: 'delete-element',
    onClick: ({ setShowDeleteBox }) => {
      setShowDeleteBox(true);
    },
  },
];

export default function ElementButtons({
  setShowFontLightBox,
  setShowImageLightBox,
  setShowBgLightBox,
  setShowDeleteBox,
  storedData,
  setStoredData,
}) {
  const addFontElement = () => {
    // const storedData = JSON.parse(localStorage.getItem('theatre-data')) || [];
    const lastFontId = storedData
      .filter((element) => element.type === 'FONT')
      .reduce((maxId, element) => {
        const idNumber = parseInt(element.id.split('-')[1]);
        return idNumber > maxId ? idNumber : maxId;
      }, 0);

    const nextFontId = `font-${String(lastFontId + 1).padStart(3, '0')}`;

    const updatedElementData = [
      ...storedData,
      { id: nextFontId, type: 'FONT' },
    ];

    localStorage.setItem('theatre-data', JSON.stringify(updatedElementData));
    setStoredData(updatedElementData);
  };
  return (
    <Wrapper>
      {buttons.map((button, index) => (
        <button
          key={index}
          className={button.className}
          onClick={() =>
            button.onClick({
              setShowFontLightBox,
              setShowImageLightBox,
              setShowBgLightBox,
              setShowDeleteBox,
              addFontElement,
            })
          }
        >
          {button.label}
        </button>
      ))}
    </Wrapper>
  );
}
