import studio from '@theatre/studio';
import { getProject, types } from '@theatre/core';
import { useState, useEffect } from 'react';
import EmbeddedCode from './embedded';
import styled from 'styled-components';
import EmbeddedBox from '../components/lightbox/embedded-box';
import Font from '../components/lightbox/font';
import ReactDOM from 'react-dom';
import projectState from '../json/state.json';
import SettingButtons from '../components/add-button/setting-button';
import ElementButtons from '../components/add-button/element-button';
import LightBox from '../components/lightbox';

const BasicButton = styled.div`
  position: fixed;
  padding: 10px;
  bottom: 0;
  left: 0;
  background: #f4dac8;
  max-width: 100px;

  button {
    padding: 5px;
    margin: 5px;
    cursor: pointer;
    background: white;
    border: 1px solid grey;
    border-radius: 5px;
  }
`;

const ElementButton = styled.div`
  position: fixed;
  padding: 10px;
  bottom: 0;
  left: 130px;
  background: #c1dafe;
  max-width: 100px;

  button {
    padding: 5px;
    background: white;
    margin: 5px;
    cursor: pointer;
    border: 1px solid grey;
    border-radius: 5px;
  }
`;

const Container = styled.div`
  min-width: 100%;
  position: relative;
  outline: 5px solid red;
  z-index: -1;
`;

const FontBox = styled.div`
  position: absolute;
  width: 300px;
  height: 20px;
  background: white;
  outline: 3px solid black;
  z-index: 10000;
  padding: 20px;
`;

//重新整理後，必帶最近一次舊資料
//抓資料：先從 CMS 調用，沒資料再看 local-storage 有沒有資料
//存資料：CMS 跟 local-storage 都存

export default function PlayPage() {
  studio.initialize();
  const project = getProject('New Project', { state: projectState });
  const sheet = project.sheet('Scene', 'default');

  studio.ui.restore();

  project.ready.then(() =>
    sheet.sequence.play({
      iterationCount: Infinity,
    })
  );

  const [boxes, setBoxes] = useState([]); //紀錄新增的資料
  const [preData, setPreData] = useState([]); //上一筆舊資料，要與 local-storage 一致

  //從 local-storage 取得舊資料
  useEffect(() => {
    const storedBoxes = localStorage.getItem('theatre-data');
    if (storedBoxes) {
      setPreData(JSON.parse(storedBoxes));
    }
  }, []);

  //render 出既有的元件
  useEffect(() => {
    preData
      .filter((item) => item.type === 'box')
      .forEach((item) => {
        const box = sheet.object(item.id, {
          position: {
            x: 0,
            y: 0,
          },
          bgColor: types.string('#ffffff'),
        });

        const element = document.getElementById(item.id);

        box.onValuesChange((newValue) => {
          element.style.left = newValue.position.x + 'px';
          element.style.top = newValue.position.y + 'px';
          element.style.backgroundColor = newValue.bgColor;
        });
      });
  }, [preData]);

  const addFontElement = (name = '', content = '') => {
    //新增一個控制項
    const box = sheet.object(`${name}`, {
      position: {
        x: 0,
        y: 0,
      },
      bgColor: types.string('#ffffff'),
    });

    const div = document.createElement('div');

    div.style.cssText = `
    position: absolute;
    width: 300px;
    height: 20px;
    background: white;
    outline: 3px solid black;
    z-index: 10000;
    padding: 20px;
  `;

    div.textContent = `${content}`;
    div.id = `${name}`;

    const container = document.getElementById('item-container');

    if (container) {
      container.appendChild(div);
    }

    let style = {};

    box.onValuesChange((newValue) => {
      div.style.left = newValue.position.x + 'px';
      div.style.top = newValue.position.y + 'px';
      div.style.fontSize = newValue.size;
      div.style.color = newValue.color;
    });

    setBoxes((prevBoxes) => [
      ...prevBoxes,
      { content, element: div, id: name, type: 'box', style: style },
    ]);
  };

  const [showFontLightBox, setShowFontLightBox] = useState(false);

  const data = [...preData, ...boxes];

  return (
    <>
      <LightBox />

      <SettingButtons sheet={sheet} data={data} />
      <ElementButtons />

      {/* 只 render local-storage 的舊資料 */}
      <Container id='item-container'>
        {preData.map((box, index) => (
          <FontBox key={index} id={box.id} style={box.style}>
            {box.content}
          </FontBox>
        ))}
      </Container>
    </>
  );
}
