import React, { useRef, useEffect } from 'react';
import styled from 'styled-components';
import studio from '@theatre/studio';
import { initialProperties } from '../constants';
import useDrag from '../hooks/use-drag';

const Wrapper = styled.div`
  width: 100%;
  height: 100vh;
  position: relative;
  background: grey;
`;

const FontElement = styled.div``;

export default function Stage({ previousData, sheet }) {
  const fontElementRefs = useRef([]);

  // const handleFontElementRef = (index, ref) => {
  //   fontElementRefs.current[index] = ref;
  // };

  // previousData.forEach((data, index) => {
  //   const dragOpts = {
  //     onDragStart: () => {
  //       studio.scrub();
  //     },
  //     onDrag: (x, y) => {
  //       const obj = sheet.object(data.id, initialProperties.FONT);
  //       studio.updateObject(obj, {
  //         position: { x: data.style.left + x, y: data.style.top + y },
  //       });
  //     },
  //     onDragEnd: (dragHappened) => {
  //       if (dragHappened) {
  //         studio.commit();
  //       } else {
  //         studio.discard();
  //       }
  //     },
  //     lockCursorTo: 'move',
  //   };

  //   useDrag(fontElementRefs.current[index], dragOpts);
  // });

  // const handleFontElementRef = (index, ref) => {
  //   fontElementRefs.current[index] = ref;
  // };

  useEffect(() => {
    previousData.forEach((data, index) => {
      const dragOpts = {
        onDragStart: () => {
          studio.scrub();
        },
        onDrag: (x, y) => {
          const obj = sheet.object(data.id, initialProperties.FONT);
          studio.updateObject(obj, {
            position: { x: data.style.left + x, y: data.style.top + y },
          });
        },
        onDragEnd: (dragHappened) => {
          if (dragHappened) {
            studio.commit();
          } else {
            studio.discard();
          }
        },
        lockCursorTo: 'move',
      };

      const fontElementRef = fontElementRefs.current[index];
      if (fontElementRef) {
        const divElement = fontElementRef.querySelector('div');
        if (divElement) {
          useDrag(divElement, dragOpts);
        }
      }
    });
  }, [previousData]);

  const fontElements = previousData.map((data, index) => {
    return (
      <FontElement
        key={index}
        id={data.id}
        style={data.style}
        onClick={() => {
          const obj = sheet.object(data.id, initialProperties.FONT);
          studio.setSelection([obj]);
        }}
        ref={(ref) => handleFontElementRef(index, ref)}
      >
        {data.content}
      </FontElement>
    );
  });
  return <Wrapper id='theatre-stage'>{fontElements}</Wrapper>;
}
