import React, { useState, useEffect } from 'react';
import styled, { keyframes } from 'styled-components';
import FontElement from './elements/font';
import ImageElement from './elements/image';
import BackgroundElement from './elements/background';
import Dimmer from './dimmer-with-message';

const Wrapper = styled.div`
  width: 100%;
  height: 100vh;
  position: relative;
  overflow: hidden;
  margin: auto;
`;

export default function Stage({ objData, sheet, stageWidth, draggable }) {
  // 如果圖片沒有全部 load 完，含 error 狀況，會一直顯示 dimmer
  const [loadedImages, setLoadedImages] = useState(0);
  const [isLoading, setIsLoading] = useState(true);

  const [hasImgError, setHasImgError] = useState(false);

  //狀況：src 初始會是 ''，就會直接進 error
  console.log('hasImgError', hasImgError);

  const totalImages = objData.filter(
    (data) => data.type === 'IMAGE' || data.type === 'BACKGROUND'
  ).length;

  const handleImageLoad = () => {
    setLoadedImages((prev) => prev + 1);
  };

  const handleImageError = () => {
    setHasImgError(true);
  };

  useEffect(() => {
    if (loadedImages === totalImages) {
      setIsLoading(false);
    }
  }, [loadedImages, totalImages]);

  // ----------------------------------------

  const fontElements = objData
    .filter((data) => data.type === 'FONT')
    .map((data) => {
      return (
        <FontElement
          key={'font' + data.id}
          id={data.id}
          sheet={sheet}
          stageWidth={stageWidth}
          draggable={draggable}
        />
      );
    });

  const imageElements = objData
    .filter((data) => data.type === 'IMAGE')
    .map((data) => {
      return (
        <ImageElement
          key={'image' + data.id}
          id={data.id}
          sheet={sheet}
          stageWidth={stageWidth}
          draggable={draggable}
          onLoad={handleImageLoad}
          onError={handleImageError}
        />
      );
    });

  const bgElements = objData
    .filter((data) => data.type === 'BACKGROUND')
    .map((data) => {
      return (
        <BackgroundElement
          key={'background' + data.id}
          id={data.id}
          sheet={sheet}
          stageWidth={stageWidth}
          draggable={draggable}
          onLoad={handleImageLoad}
          onError={handleImageError}
        />
      );
    });

  return (
    <>
      {/* {loadingJsx} */}
      {/* <Dimmer
        show={isLoading && !hasImgError}
        message={'載入中'}
        shining={true}
      /> */}
      <Wrapper id='theatre-stage'>
        <video controls width='640' height='360' autoplay='autoplay'>
          <source
            src='https://ffb19a9f2aff94c6a9e24969dbcf363e4e0cbb745fcfad9f1016a1e-apidata.googleusercontent.com/download/storage/v1/b/v3-statics-staging.mirrormedia.mg/o/files%2F20161130141317-3863a0e1b9a81cc40dee4f281fc70274.mp4?jk=ATmLuOHXuHxWbVU8JWgUlI4s68mJHKgKSSQMjI1Mm_VJiPNj9idilsxm9L7RbSyLKF3_899DG90khx1hArulWkJkJnBpY-tsLqrPgJCkfqWsTCq8wcKvgAO72dSCq03ufkhcWJIohC2nYGGUq8Q6G8Lxn07_k_x_4yISHCsWoVj3f0flPmAp5mceL6PCmW2JKlCdYCy210D0y5fYJfCJ8_5qZ-_2WMnXWkAOjKJ9Li6oMFWfncouVil9KjQ27krdTs3uIdmGhk8oft4pOhAJGGbA3vpQA1YP3_740shLj_JssG0FlOWWjakidPPaNJHESP5tZgdfaq7yzjr57uMt4HD_WmC1wXF77W8ueFBDeXUzLfOZrwis7iiGc96y-qnQj5_rPK7kASHy24wEreT3Dmbyftfx-kZiGkEaYl-ZxzKgbG1UMtdWv4RMl1C5-mk2zqLxl4UGUY0ZlBJzFMz1WtxtwfBMMvncEitZyHyLW0fYB66_aCYZYFE3MOJ-IqTe06sCDHZydEERbFlYOeZgCAR5920eF_lFiVHzTQOitpYNvjnw2jZRx8BcAw8H17PHvo3s5RiIapSIGm4nA8W_ap2Voz31dw3CeRagB8cI1JflElr10-6pDODLK45Ai3nScQzeb-0Cc9GWlA5T6s09Y39bZf4v32R36awlH8_EGuf97YpJqQWx5P36chRLRcGNhyyd_h2Dt4jGkmOE65Y4drIPdPpg-7EwO9soyid_R7fkn_OwIk3lBa5_YRpLbYVyTH77RJqSGsUASyFs6tRNFvmknZhtxlmUCoQAMNghEjZzTw3SgdjWsSgCrOQ1crXjSKa-cWK6UnDlmlfmJQBCgogYRHrOIoKDaldeUlU9hhkqEkNQJNyp-0Bka-eau4g6QvpI-VQKUNEEL-dKI-ihYM8JpWEa6lzZ0cmOFPpYxXMmHO2g5sEndzDW1qOwN7OYYcT_a0054rlKs5-yqzn_L3XW98Eas5-tZUtdfrrS0XU9tGpD8OldDtJQI_Dhs7t78bm0b2V7HwnJnyuyVbUIoSg48_h-vWGLhyzU30h45nxU7z3l_ZjehPtNuV9PTk3UzdvkCaKWRCAAJLF4rdK3f7QRnGTrxEelT-hgqaQb24gi0tvtAsWhBuuiXMETUnh3npfHXd2BHdxuOFjOOt_-LAOMoHXpS4UC5JNHDWJnTE0J-bGb8WkY-VBmQhSFwfLSxI1H8mUoNqXG_NxJeWtjfbdL8XBw8k7DZetkUkivsvvJhJ4FrssC3kjTtq0huK0QWluygATcxlYTRt78JR8jmZJMfQ&isca=1'
            type='video/mp4'
          />
        </video>
        {/* {fontElements}
        {imageElements}
        {bgElements} */}
      </Wrapper>
      {/* <Dimmer
        show={hasImgError}
        message={'載入失敗。請檢查您的網路連線，並重新整理瀏覽器。'}
      /> */}
      ;
    </>
  );
}
