import React, { useState } from 'react';
import styled from 'styled-components';

const Wrapper = styled.div`
  width: 100%;
  height: 100vh;
  background: black;
  opacity: 1;
  display: flex;
  justify-content: center;
  align-items: center;
  position: fixed;
  top: 0;
  left: 0;
  z-index: 200;
`;

const Form = styled.div`
  background: white;
  border: 10px;
  padding: 20px;
  outline: 3px solid black;
  margin: 70px auto;
  padding: 50px;

  input {
    width: 50%;
    margin-bottom: 20px;
  }

  button {
    padding: 5px;
    background: black;
    color: white;
    outline: none;
    cursor: pointer;
    margin-top: 10px;
    margin-right: 10px;
  }

  h2 {
    margin-top: 0;
  }
`;

export default function ImageLightBox({
  setShowImageLightBox,
  storedData,
  setStoredData,
}) {
  const [imageID, setImageID] = useState('');

  const addImageElement = (id = '') => {
    const updatedElementData = [
      ...storedData,
      // { id: id, url: url, type: 'IMAGE' },
      { id: id, type: 'IMAGE' },
    ];

    localStorage.setItem('theatre-data', JSON.stringify(updatedElementData));
    setStoredData(updatedElementData);
  };

  return (
    <Wrapper>
      <Form>
        <h2>新增 Image 元件 </h2>

        <span>物件 ID (勿與之前的重複)： </span>
        <input
          placeholder='中/英/數字皆可'
          value={imageID}
          onChange={(e) => setImageID(e.target.value)}
        />

        {/* <textarea
          placeholder='圖片連結'
          value={imageURL}
          onChange={(e) => setImageUrl(e.target.value)}
        /> */}

        <button
          onClick={() => {
            setShowImageLightBox(false);
            // addImageElement(imageID, imageURL);
            addImageElement(imageID);
          }}
        >
          確定
        </button>

        <button
          onClick={() => {
            setShowImageLightBox(false);
          }}
        >
          取消
        </button>
      </Form>
    </Wrapper>
  );
}
