import { types } from '@theatre/core';

export const initialProperties = {
  FONT: {
    position: types.compound({
      x: types.number(0),
      y: types.number(0),
    }),

    color: types.compound({
      background: types.rgba({ r: 255, g: 0, b: 0, a: 1 }),
      border: types.rgba({ r: 255, g: 0, b: 0, a: 1 }),
      font: types.rgba({ r: 255, g: 0, b: 0, a: 1 }),
    }),
    bgColor: types.string('#ffffff'),
    borderColor: types.string('#ffffff'),
    size_px: 0,
    message: types.string('Animation Loading', {
      // override the label given in the Details Panel and DopeSheet
      label: 'The Message',
    }),
  },

  IMAGE: {
    position: {
      x: 0,
      y: 0,
    },
    bgColor: types.string('#ffffff'),
  },
};
