import React from 'react';
import styled from 'styled-components';

const Wrapper = styled.div`
  padding: 10px;
  background: #c1dafe;
  max-width: 100px;

  button {
    padding: 5px;
    background: white;
    margin: 5px;
    cursor: pointer;
    border: 1px solid grey;
    border-radius: 5px;
  }
`;

const addFontElement = (fontID = '', fontContent = '') => {
  const menuConfig = sheet.object(`${fontID}`, {
    ...initialConfig.FONT,
    content: types.string(`${fontContent}`),
  });

  console.log('有執行addFontElement');

  const div = document.createElement('div');

  div.id = `${fontID}`;
  div.textContent = `${fontContent}`;
  div.onclick = () => studio.setSelection([menuConfig]);
  div.style.cssText = `
    position: absolute;
    z-index: 1000;
    display: flex;
    align-items: center;
    justify-content: center'
    cursor: pointer;
  `;

  const container = document.getElementById('theatre-stage');
  if (container) {
    container.appendChild(div);
  }

  let updateStyle;

  menuConfig.onValuesChange((newValue) => {
    console.log('newValue', newValue);
    div.style.left = newValue.position.x + '%';
    div.style.top = newValue.position.y + '%';
    div.style.background = newValue.bgColor;
    div.style.color = newValue.font.color;
    div.style.opacity = newValue.opacity;
    div.style.padding = newValue.padding + 'px';
    div.style.display = newValue.visible ? 'block' : 'none';
    div.style.width = newValue.size.width + 'px';
    div.style.height = newValue.size.height + 'px';
    div.style.fontSize = newValue.font.size + 'px';
    div.style.borderRadius = newValue.border.radius + '%';
    div.style.border = `${newValue.border.size}px solid ${newValue.border.color}`;

    updateStyle = {
      position: 'absolute',
      zIndex: '1000',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      cursor: 'pointer',
      left: newValue.position.x + '%',
      top: newValue.position.y + '%',
      color: newValue.font.color,
      fontSize: newValue.font.size + 'px',
      background: newValue.bgColor,
      padding: newValue.padding + 'px',
      display: newValue.visible ? 'block' : 'none',
      width: newValue.size.width + 'px',
      height: newValue.size.height + 'px',
      opacity: newValue.opacity,
      borderRadius: newValue.border.radius + '%',
      border: newValue.border.size + 'px solid' + newValue.border.color,
    };

    div.textContent = newValue.content;
  });

  console.log('updateStyle', updateStyle);

  setElementData((prev) => [
    ...prev,
    {
      content: fontContent,
      id: fontID,
      type: 'font',
      style: updateStyle,
    },
  ]);
};

const buttons = [
  {
    label: '新增 - 背景',
    className: 'add-background',
    onClick: () => {
      // 处理新增背景的逻辑
    },
  },
  {
    label: '新增 - 文字',
    className: 'add-font',
    onClick: ({ setShowFontLightBox }) => {
      setShowFontLightBox(true);
    },
  },
  {
    label: '新增 - 圖片',
    className: 'add-image',
    onClick: ({ setShowFontLightBox }) => {
      setShowFontLightBox(true);
    },
  },
  {
    label: '刪除元件',
    className: 'delete-element',
    onClick: () => {
      // 处理删除元件的逻辑
    },
  },
];

export default function ElementButtons({ setShowFontLightBox }) {
  return (
    <Wrapper>
      {buttons.map((button, index) => (
        <button
          key={index}
          className={button.className}
          onClick={() => button.onClick({ setShowFontLightBox })}
        >
          {button.label}
        </button>
      ))}
    </Wrapper>
  );
}
