import React, { useState, useEffect } from 'react';
import styled, { keyframes } from 'styled-components';
import FontElement from './elements/font';
import ImageElement from './elements/image';
import BackgroundElement from './elements/background';
import Dimmer from './dimmer-with-message';

const Wrapper = styled.div`
  width: 100%;
  height: 100vh;
  position: relative;
  overflow: hidden;
  margin: auto;
`;

export default function Stage({ objData, sheet, stageWidth, draggable }) {
  const [loadedImages, setLoadedImages] = useState(0);
  const [isLoading, setIsLoading] = useState(true);
  const [hasImgError, setHasImgError] = useState(false);

  const totalImages = objData.filter(
    (data) => data.type === 'IMAGE' || data.type === 'BACKGROUND'
  ).length;

  // const handleImageLoad = () => {
  //   setLoadedImages((prev) => prev + 1);
  // };

  // const handleImageError = () => {
  //   setHasImgError(true);
  // };

  useEffect(() => {
    if (loadedImages === totalImages) {
      setIsLoading(false);
    }
  }, [loadedImages, totalImages]);

  // ----------------------------------------

  // let loadingJsx = null;

  // if (hasImgError) {
  //   loadingJsx = (
  //     <Dimmer
  //       show={hasImgError}
  //       message={'載入失敗。請檢查您的網路連線，並重新整理瀏覽器。'}
  //     />
  //   );
  // } else if (isLoading) {
  //   loadingJsx = <Dimmer show={isLoading} message={'載入中'} shining={true} />;
  // }

  // ----------------------------------------

  const fontElements = objData
    .filter((data) => data.type === 'FONT')
    .map((data) => {
      return (
        <FontElement
          key={'font' + data.id}
          id={data.id}
          sheet={sheet}
          stageWidth={stageWidth}
          draggable={draggable}
        />
      );
    });

  const imageElements = objData
    .filter((data) => data.type === 'IMAGE')
    .map((data) => {
      return (
        <ImageElement
          key={'image' + data.id}
          id={data.id}
          sheet={sheet}
          stageWidth={stageWidth}
          draggable={draggable}
          onLoad={() => setLoadedImages((prev) => prev + 1)}
          onError={() => setHasImgError(true)}
        />
      );
    });

  const bgElements = objData
    .filter((data) => data.type === 'BACKGROUND')
    .map((data) => {
      return (
        <BackgroundElement
          key={'background' + data.id}
          id={data.id}
          sheet={sheet}
          stageWidth={stageWidth}
          draggable={draggable}
          onLoad={() => setLoadedImages((prev) => prev + 1)}
          onError={() => setHasImgError(true)}
        />
      );
    });

  return (
    <>
      {/* {loadingJsx} */}
      {/* {isLoading && !hasImgError && ( */}
      <Dimmer
        show={isLoading && !hasImgError}
        message={'載入中'}
        shining={true}
      />
      {/* )} */}
      <Wrapper id='theatre-stage'>
        {fontElements}
        {imageElements}
        {bgElements}
      </Wrapper>
      ;
    </>
  );
}
