import React, { useState } from 'react';
import styled from 'styled-components';
import { initialConfig } from '../../constants';
import studio from '@theatre/studio';
import { types } from '@theatre/core';
import { defaultFontStyle } from '../shared-style';

const Wrapper = styled.div`
  width: 100%;
  height: 100vh;
  background: black;
  opacity: 1;
  display: flex;
  justify-content: center;
  position: fixed;
  top: 0;
  left: 0;
  z-index: 200;
`;

const Form = styled.div`
  width: 30%;
  height: 50%;
  background: white;
  border: 10px;
  padding: 20px;
  outline: 3px solid black;
  margin: 70px auto;

  input {
    width: 30%;
    margin-bottom: 20px;
  }

  textarea {
    width: 90%;
    height: 200px;
  }

  button {
    padding: 5px;
    background: black;
    color: white;
    outline: none;
    cursor: pointer;
    margin-top: 10px;
    margin-right: 10px;
  }
`;

export default function FontLightBox({ setShowFontLightBox, sheet }) {
  const [fontContent, setFontContent] = useState('');
  const [fontID, setFontID] = useState('');

  // 改為只新增預設值元件？內容都後後面再調？？
  const addFontElement = (fontID = '', fontContent = '') => {
    const menuConfig = sheet.object(`${fontID}`, {
      ...initialConfig.FONT,
      content: types.string(`${fontContent}`),
    });

    const element = document.createElement('div');

    element.id = `${fontID}`;
    element.textContent = `${fontContent}`;
    element.onclick = () => {
      studio.setSelection([menuConfig]);
    };
    element.style.cssText = `
      position: absolute;
      display: flex;
      align-items: center;
      justify-content: center'
      word-wrap: break-word;
      transform-origin: center;
    `;

    // element.style.cssText = defaultFontStyle;

    const container = document.getElementById('theatre-stage');
    if (container) {
      container.appendChild(div);
    }

    menuConfig.onValuesChange((newValue) => {
      element.style.left = newValue.position.x + '%';
      element.style.top = newValue.position.y + '%';
      element.style.background = newValue.bgColor;
      element.style.color = newValue.font.color;
      element.style.opacity = newValue.opacity;
      element.style.padding = newValue.padding + 'px';
      element.style.display = newValue.visible ? 'block' : 'none';
      element.style.width = newValue.size.width + 'px';
      element.style.fontSize = newValue.font.size + 'px';
      element.style.borderRadius = newValue.border.radius + '%';
      element.style.border = `${newValue.border.size}px solid ${newValue.border.color}`;
      element.textContent = newValue.content;
      setFontContent(newValue.content);

      element.style.fontWeight = newValue.font.weight;
      element.style.letterSpacing = newValue.font.spacing + 'px';
      element.style.lineHeight = newValue.lineHeight + 'px';
      element.style.textAlign = newValue.textAlign;
      element.style.zIndex = newValue.zIndex;
      element.style.transform = `scale(${newValue.scale})`;
    });

    const theatreData = JSON.parse(localStorage.getItem('theatre-data')) || [];
    const updatedElementData = [
      ...theatreData,
      { id: fontID, content: fontContent, type: 'FONT' },
    ];
    localStorage.setItem('theatre-data', JSON.stringify(updatedElementData));
  };

  return (
    <Wrapper>
      <Form>
        <h2>新增 Font 元件 </h2>

        <span>物件 ID (勿與之前的重複)： </span>
        <input
          placeholder='中/英/數字皆可'
          value={fontID}
          onChange={(e) => setFontID(e.target.value)}
        />

        <textarea
          placeholder='文字內容'
          value={fontContent}
          onChange={(e) => setFontContent(e.target.value)}
        />

        <button
          onClick={() => {
            setShowFontLightBox(false);
            addFontElement(fontID, fontContent);
          }}
        >
          確定
        </button>

        <button
          onClick={() => {
            setShowFontLightBox(false);
          }}
        >
          取消
        </button>
      </Form>
    </Wrapper>
  );
}
