import React, { useState } from 'react';
import styled from 'styled-components';
import ElementButtons from './button/element-button';
import SettingButtons from './button/setting-button';
import JSONLightBox from './lightbox/show-json';
import DeleteLightBox from './lightbox/delete-obj';
import EditJSONLightBox from './lightbox/edit-json';

const LightBoxWrapper = styled.div`
  position: relative;
`;

const ButtonWrapper = styled.div`
  position: fixed;
  bottom: 0;
  left: 0;
  display: flex;
  justify-content: space-between;
  z-index: 100;
`;

export default function ControlPanel({
  sheet,
  setStoredData,
  storedData,
  showPanel,
}) {
  //Lightbox
  const [showJSONLightBox, setShowJSONLightBox] = useState(false);
  const [showDeleteBox, setShowDeleteBox] = useState(false);
  const [showEditJSON, setShowEditJSON] = useState(false);

  //JSON data
  const [animateJSON, setAnimateJSON] = useState({}); // 存在 storage 的 objData
  const [elementJSON, setElementJSON] = useState([]); // 存在 storage 的 animateData

  return (
    <>
      <LightBoxWrapper id='lightbox-wrapper'>
        {showBgLightBox && (
          <BgLightBox
            setShowBgLightBox={setShowBgLightBox}
            storedData={storedData}
            setStoredData={setStoredData}
          />
        )}

        {showJSONLightBox && (
          <JSONLightBox
            setShowJSONLightBox={setShowJSONLightBox}
            setAnimateJSON={setAnimateJSON}
            animateJSON={animateJSON}
            elementJSON={elementJSON}
            setElementJSON={setElementJSON}
          />
        )}

        {showEditJSON && <EditJSONLightBox setShowEditJSON={setShowEditJSON} />}

        {showDeleteBox && (
          <DeleteLightBox
            storedData={storedData}
            setStoredData={setStoredData}
            setShowDeleteBox={setShowDeleteBox}
            sheet={sheet}
          />
        )}
      </LightBoxWrapper>

      {showPanel && (
        <ButtonWrapper>
          <SettingButtons
            sheet={sheet}
            setShowJSONLightBox={setShowJSONLightBox}
            setElementJSON={setElementJSON}
            setAnimateJSON={setAnimateJSON}
            setShowEditJSON={setShowEditJSON}
          />
          <ElementButtons
            setShowFontLightBox={setShowFontLightBox}
            setShowImageLightBox={setShowImageLightBox}
            setShowBgLightBox={setShowBgLightBox}
            setShowDeleteBox={setShowDeleteBox}
            setStoredData={setStoredData}
            storedData={storedData}
          />
        </ButtonWrapper>
      )}
    </>
  );
}
