import React, { useState, useMemo } from 'react';
import styled from 'styled-components';
import {
  defaultFontStyle,
  defaultImageStyle,
  defaultBgStyle,
} from '../shared-style/index';
import FontElement from './elements/font';
import ImageElement from './elements/image';

const Wrapper = styled.div`
  width: 100%;
  height: 100vh;
  position: relative;
  overflow: hidden;
  margin: auto;
`;

// const ImageElement = styled.div`
//   ${defaultImageStyle};
// `;

const BgElement = styled.div`
  ${defaultBgStyle};
`;

export default function Stage({ objData, sheet, screenWidth }) {
  // 處理拖拉功能

  // --------------------------------

  // const fontElements = objData
  //   .filter((data) => data.type === 'FONT')
  //   .map((data, index) => {
  //     return <FontElement key={index} id={data.id} ref={setDivRef} />;
  //   });

  const fontElements = objData
    .filter((data) => data.type === 'FONT')
    .map((data) => {
      return (
        <FontElement
          key={'font' + data.id}
          id={data.id}
          sheet={sheet}
          screenWidth={screenWidth}
        />
      );
    });

  // const imageElements = objData
  //   .filter((data) => data.type === 'IMAGE')
  //   .map((data, index) => {
  //     return (
  //       <ImageElement key={index} id={data.id}>
  //         <img src='/default-image.png' alt={data.id} />
  //       </ImageElement>
  //     );
  //   });

  const imageElements = objData
    .filter((data) => data.type === 'IMAGE')
    .map((data) => {
      return (
        <ImageElement
          key={'image' + data.id}
          id={data.id}
          sheet={sheet}
          screenWidth={screenWidth}
        />
      );
    });

  const bgElements = objData
    .filter((data) => data.type === 'BACKGROUND')
    .map((data, index) => {
      return (
        <BgElement key={index} id={data.id}>
          <img src='/default-image.png' alt={data.id} />
        </BgElement>
      );
    });

  // const stageWidth = stageSize ? stageSize + 'px' : '100vw';

  return (
    <Wrapper id='theatre-stage'>
      {fontElements}
      {/* {imageElements} */}
      {bgElements}
    </Wrapper>
  );
}
