import React, { useState } from 'react';
import styled from 'styled-components';

const Wrapper = styled.div`
  width: 100%;
  height: 100vh;
  background: black;
  opacity: 1;
  display: flex;
  justify-content: center;
  align-items: center;
  position: fixed;
  top: 0;
  left: 0;
  z-index: 100;
`;

const Form = styled.div`
  background: white;
  border: 10px;
  padding: 10px 30px 30px;
  outline: 3px solid black;
  display: flex;

  textarea {
    width: 100%;
    height: 100px;
    margin-bottom: 10px;
  }

  button {
    padding: 10px;
    background: black;
    color: white;
    outline: none;
    cursor: pointer;
    margin-top: 10px;
    margin-right: 10px;
  }
`;

const Block = styled.div`
  margin: 0 30px;
  width: 400px;
`;

export default function JSONLightBox({
  setShowJSONLightBox,
  animateJSON,
  objData,
  setObjData,
  setState,
}) {
  const [inputObj, setInputObj] = useState('');
  const [inputAnimate, setInputAnimate] = useState('');

  return (
    <Wrapper>
      <Form>
        <Block>
          <h2> JSON 資料 (Read only) </h2>
          <p>元件 JSON</p>
          <textarea
            placeholder='元件JSON'
            value={JSON.stringify(objData)}
            readOnly={true}
          />

          <p>動畫 JSON</p>

          {/* 如果 animateJSON 沒有經過 JSON.stringify，那就會是 [object, object] */}
          <textarea
            placeholder='動畫JSON'
            value={JSON.stringify(animateJSON)}
            readOnly={true}
          />
        </Block>

        {/* 這是新的 block */}
        <Block>
          <h2> Edit JSON </h2>
          <p>元件 JSON</p>
          <textarea
            placeholder='貼上要讀取的元件JSON'
            value={inputObj}
            onChange={(e) => setInputObj(e.target.value)}
          />

          <p>動畫 JSON</p>

          {/* 如果 animateJSON 沒有經過 JSON.stringify，那就會是 [object, object] */}
          <textarea
            placeholder='貼上要讀取的動畫JSON'
            value={inputAnimate}
            onChange={(e) => setInputAnimate(e.target.value)}
          />

          <button
            onClick={() => {
              setShowJSONLightBox(false);
            }}
          >
            Close
          </button>

          <button
            onClick={() => {
              setShowJSONLightBox(false);

              //更新 storage 資料（自動記憶）
              localStorage.setItem('theatre-data', inputObj);
              //把 render 資料換成 inputObj，讓畫面直接更新
              setObjData(JSON.parse(inputObj));

              //方法二：直接取代 storage 的資料？

              const animate = localStorage.getItem('theatre-0.4.persistent');

              const animateState =
                JSON.parse(animate)?.historic?.innerState?.coreByProject
                  ?.Project ?? {};

              //在 window.reload 之後，state 會重回 {}
              // localStorage.setItem('theatre-state', inputAnimate);
              setState(inputAnimate);

              // window.location.reload();
            }}
          >
            儲存修改
          </button>
        </Block>
      </Form>
    </Wrapper>
  );
}
