import React, { useState } from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom';
import JSONLightBox from '../lightbox/show-json';

const Wrapper = styled.div`
  padding: 5px;
  background: rgba(255, 255, 255, 0.95);
  max-width: 100px;
  border-radius: 10px;
  margin: 10px 0 10px 10px;
  outline: 2px solid #e9e9e9;

  button {
    padding: 5px;
    margin: 5px;
    cursor: pointer;
    background: #e9e9e9;
    border: none;
    border-radius: 5px;
  }
`;

const buttons = [
  {
    label: 'clear JSON',
    onClick: () => {
      const confirmation = confirm('將清空 project 所有資料');

      if (confirmation) {
        localStorage.removeItem('theatre-data');
        localStorage.removeItem('theatre-0.4.persistent');
        window.location.reload();
      } else {
        return;
      }
    },
  },

  // 動畫 JSON 一直在默默更新，需要 show 時，要再一次執行拿的動作以進行更新
  {
    label: 'show JSON',
    onClick: ({
      setShowJSONLightBox,
      setElementJSON,
      setAnimateJSON,
      getAnimateJSONState,
    }) => {
      // get latest JSON data from local-storage
      // const element = localStorage.getItem('theatre-data');

      //按下按鈕時要執行
      // const animate = localStorage.getItem('theatre-0.4.persistent');

      // const animateState =
      //   JSON.parse(animate)?.historic?.innerState?.coreByProject?.Project ?? {};

      // setAnimateJSON(animateState);
      // setElementJSON(element);
      getAnimateJSONState();
      setShowJSONLightBox(true);
    },
  },
];

export default function SettingButtons({ sheet, setShowEditJSON }) {
  const [animateJSON, setAnimateJSON] = useState({});
  const [showJSONLightBox, setShowJSONLightBox] = useState(false);

  function getAnimateJSONState() {
    const animate = localStorage.getItem('theatre-0.4.persistent');
    const animateState =
      JSON.parse(animate)?.historic?.innerState?.coreByProject?.Project ?? {};
    setAnimateJSON(animateState);
  }

  return (
    <Wrapper>
      {buttons.map((button, index) => (
        <button
          key={index}
          onClick={() =>
            button.onClick({
              sheet,
              setShowJSONLightBox,
              getAnimateJSONState,
              // setElementJSON,
              // setAnimateJSON,
              setShowEditJSON,
            })
          }
        >
          {button.label}
        </button>
      ))}

      <Link to='/demo-scroll' target='_blank'>
        <button>Scroll</button>
      </Link>

      <Link to='/demo-video' target='_blank'>
        <button>Video</button>
      </Link>
    </Wrapper>
  );
}
