import studio from '@theatre/studio';
import { val, getProject } from '@theatre/core';
import { useState, useEffect, useRef } from 'react';
import styled, { keyframes } from 'styled-components';
import Stage from '../components/stage';
// import Dimmer from '../components/dimmer-with-message';

const ViewBox = styled.div`
  position: relative;
  width: 100vw;
  min-width: 100vw;
  max-width: 100vw;
  min-height: 100vh;
  background: #ffffff;
`;

const StageWrapper = styled.div`
  width: 100%;
  height: 100vh;
  position: sticky;
  top: 0;
  left: 0;
`;

const circle = keyframes`
  0% {
    transform: rotate(0deg);
  }

  100% {
    transform: rotate(360deg);
  }
`;

const SpinnerBlock = styled.div`
  position: absolute;
  left: 50%;
  top: 50%;
  transform: translate(-50%, -50%);
  width: 5%;

  & > svg {
    animation: 1s ${circle} linear infinite;
  }

  @media screen and (max-width: 767px) {
    width: 10%;
  }
`;

function Spinner() {
  return (
    <SpinnerBlock>
      <svg
        viewBox='0 0 20 20'
        version='1.1'
        xmlns='http://www.w3.org/2000/svg'
        xmlnsXlink='http://www.w3.org/1999/xlink'
      >
        <g stroke='none' strokeWidth='1' fill='none' fillRule='evenodd'>
          <g fill='#fff' fillRule='nonzero'>
            <path d='M10,3.5 C6.41015,3.5 3.5,6.41015 3.5,10 C3.5,10.4142 3.16421,10.75 2.75,10.75 C2.33579,10.75 2,10.4142 2,10 C2,5.58172 5.58172,2 10,2 C14.4183,2 18,5.58172 18,10 C18,14.4183 14.4183,18 10,18 C9.58579,18 9.25,17.6642 9.25,17.25 C9.25,16.8358 9.58579,16.5 10,16.5 C13.5899,16.5 16.5,13.5899 16.5,10 C16.5,6.41015 13.5899,3.5 10,3.5 Z'></path>
          </g>
        </g>
      </svg>
    </SpinnerBlock>
  );
}

const defaultScrollScale = 1500;

export default function DemoScroll() {
  studio.initialize();
  studio.ui.hide();
  const project = getProject('Project', {});
  const sheet = project.sheet('Scene', 'default');

  project.ready.then(() => sheet.sequence.pause());

  // --------------------------
  // TODO: 增加條件，減少 event-listener 偵測時間

  const [scrollPosition, setScrollPosition] = useState(0);

  const sequenceLength = val(sheet.sequence.pointer.length); //播放總時間長度
  const stageRef = useRef(null);

  useEffect(() => {
    const handleScroll = () => {
      const stage = stageRef.current;
      const viewbox = stage.closest('.theatre-scroll-viewbox');

      const stageRect = stage.getBoundingClientRect();
      const viewboxRect = viewbox.getBoundingClientRect();

      const distance = stageRect.top - viewboxRect.top;
      setScrollPosition(distance);
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  useEffect(() => {
    const newPosition =
      (scrollPosition / (sequenceLength * defaultScrollScale)) * sequenceLength;
    sheet.sequence.position = newPosition;
  }, [scrollPosition]);

  const objJson = localStorage.getItem('theatre-data'); // get objectJSON in local-storage
  const objData = objJson ? JSON.parse(objJson) : [];

  // ----------------------------

  return (
    <ViewBox
      className='theatre-scroll-viewbox'
      style={{ height: `${sequenceLength * defaultScrollScale}px` }}
    >
      <StageWrapper ref={stageRef}>
        {loadingJsx}
        <Stage
          objData={objData}
          sheet={sheet}
          stageWidth={window.innerWidth}
          draggable={false}
        />
      </StageWrapper>
    </ViewBox>
  );
}
